style.css:
@-moz-document url-prefix("https://osu.ppy.sh/beatmapsets/") {
/* Font */

/* Because I find the new website's font hard to read.*/
  .beatmap-discussion-post__message
  {
    font-family: "Arial Grande",Tahoma,Helvetica,Arial,sans-serif;
    line-height: 1.35;
    font-size: 12px;
  }



/* Images */

/* This is for places that use the triangle background image. I replaced it with a purple tinted variant. */
  .beatmap-discussion-new-float__content, .beatmap-discussion-new-float
  {
    background-image: url("https://i.imgur.com/XTy8WUS.png");
  }
  .beatmap-discussion-new-float__content:before
    {
    background-color: rgba(0,0,0,.75);
    }
  .osu-layout--body:before
  {
    background-image: url("https://i.imgur.com/XTy8WUS.png");
  }

    .page-extra-tabs-before
  {
    background-color: #2a2a3e;
  }
    .osu-layout__section--extra
{
   background-color: rgba(0,0,0,.75)
}
    .page-extra-tabs
  {
    border-bottom: 1px solid #e852a4;
    background-color: #2a2a3e;
  }
/* The button for replies and posting discussions.*/
/* Old Button image -> https://i.imgur.com/t5t5BgD.png */
  .btn-osu-big
  {
    background-image: url("https://i.imgur.com/v0iTiyd.png");
  }


/* Top Section */
/* Color for the tab links, both the modes and the general/timeline tabs */
  .page-mode-link__stripe
  {
    background-color: #e852a4;
  }
/* BG area around mapper name, watch button, etc. */
  .beatmap-discussions-header-bottom
  {
    background-color: #443d4f;
  }
/* Hype train and nomination bars */
  .bar--beatmapset-nomination-on
  {
    background-color: #e852a4;
  }
/* Area around hype train and nomination bars */
  .beatmap-discussion-nomination
  {
    background-color: #2a2836;
  }


/* Top timeline */
  .beatmap-discussions-chart__chart-area
  {
    fill: #35254f;
  }

  .beatmap-discussions-chart__axis
  {
    fill: #443d4f;
  }
/* Bottom area of timeline where symbols appear */
  .beatmap-discussions-chart__label-area
  {
    fill: #2a2836;
  }


/* Right below the new discussion textbox */
  .beatmap-discussion-new__footer
  {
    background-color: #443d4f;
  }
/* Message about remaining hype and timeline tip */
  .beatmap-discussion-new__timestamp-col, .beatmap-discussion-new__notice
  {
    background-color: #665771;
  }

  .beatmap-discussion-new__timestamp-col--label
  {
    background-color: #2a2836;
  }


/* Background color for the filter buttons (Mine / Notes / Resolved etc.)*/
  .counter-box--beatmap-discussions
  {
    background-color: #2a2836;
  }
/* BG for difficulty and user selection dropdown */
  .beatmap-list__item--selected, .beatmap-list__selector, .beatmap-discussions-user-filter__select, .beatmap-discussions-user-filter--selecting 
  {
    background-color: #2a2836;
  }
 .beatmap-discussions-user-filter__selector{
    background-color: #222;
}

  .beatmap-discussions-user-filter__item
  {
        color: #fff !important;
  }
  .beatmap-discussions-user-filter__item--owner
  {
    background-color:#662448;
  }
  .beatmap-discussions-user-filter__item--bng
  {
    background-color: #35254f;
  }
    .beatmap-discussions-user-filter__item--qat
    {
        background-color: #7e0825;   
    }
    .beatmap-discussions-user-filter__item:hover
    {
        background-color:#000;
    }
    .beatmap-discussions-user-filter__item--admin
    {
        background-color: #21424e;
    }

/* The discussions themselves. */

/* Background color */
  .beatmap-discussions__discussions
  {
    background-color: #e8e5f7;
  }

/* Just below each post, the reply area. */
  .beatmap-discussion-post--new-reply
  {
    background-color: #f6f4f9;
  }

/* Color for the mapper, such as their name and tag. */
  .beatmap-discussion-post--owner .beatmap-discussion-post__user-badge, .beatmap-discussion-post--owner .beatmap-discussion-post__user-stripe
  {
    background-color: #a23972;
  }

  .beatmap-discussion-post--owner .beatmap-discussion-post__user-text
  {
    color: #a23972;
  }

/* Remove min width*/

.beatmap-discussion-post__user{
    min-width:0;}

/* Color for the "Resolved" lines. Both the text and the color on the left side of each resolved post. */
  .beatmap-discussion__line--resolved
  {
    background-color: #a23972;
  }

  .beatmap-discussion-system-post--resolved
  {
    color: #a23972;
  }

  .beatmap-discussion-system-post--resolved:after
  {
    background-color: #a23972;
  }


/* Colors for the Resolved - Problem - Suggestion symbols, both on the timeline and the ones next to the posts. */
  .beatmap-discussion-timestamp__icon--resolved
  {
    color: #e852a4;
  }

  .beatmap-discussions-chart__point--resolved
  {
    fill: #e852a4;
  }

  .beatmap-discussion-message-type--problem
  {
    color: #e28e5d;
  }

  .beatmap-discussions-chart__point--problem
  {
    fill: #e28e5d;
  }

  .beatmap-discussion-message-type--suggestion
  {
    color: #b993ff;
  }

  .beatmap-discussions-chart__point--suggestion
  {
    fill: #b993ff;
  }


/* Upvote button. */
  .beatmap-discussion-vote--up, .beatmap-discussion-vote--up:hover
  {
    color: #e852a4;
  }

  .beatmap-discussion-vote--inactive
  {
    color: #999;
  }


/* Revised colors for the Pin button. Makes it clearer when it's turned off */
.btn-circle {
		background-color: #777;
}
.btn-circle:hover {
		background-color: #866199;
    	color: #fff;   
}
.btn-circle--activated, .btn-circle.js-activated {
		background-color: #42095f;
    	color: #fff;
}


/* History tab items */
.beatmapset-event__icon--nominate, .beatmapset-event__icon--kudosu-gain, .beatmapset-event__icon--qualify {
		background-color: #f497d4;
}
.beatmapset-event__icon--issue-resolve{
		background-color: #a255a9;
}
.beatmapset-event__icon--disqualify, .beatmapset-event__icon--kudosu-lost, .beatmapset-event__icon--nomination-reset{
		background-color: #f4cf36;
}
}
