style.css:
@-moz-document domain("larian.com") {
a,
.navigation a:link,
.t_inner,
.subjecttable,
.time {
    color: #bd0000;
}
.navigation a:visited,
.newtotal,
.forumtitle,
.date,
.author-content,
.post-content,
.post-options,
.ubbcode-body,
.ubbcode-header,
.alt-2,
.alt-1,
.small {
    color: #616161;
}
.category {
    background-color: #ffffffa8;
    color: #616161;
}
.breadcrumbs {
    color: #ff7d7d;
}
.t_inner > tbody {
    background-color: #ffffffe0;
}
.tdheader {
    color: #bd0000;
    border-bottom: 1px solid #ccc;
}
.newinforum {
    color: #ff7d7d;
    border-bottom: 1px solid #ccc;
}
.threadtotal,
.forumtitle,
.posttotal,
.posttime {
    border-bottom: 1px solid #ccc;
    border-left: 1px solid #ccc;
}
.t_standard {
    background: rgba(0, 0, 0, 0.9);
    background-color: #d4d4d4;
}
.newintopic,
.topicicon,
.alt-topicicon,
.new-topicicon,
.new-alt-topicicon,
.alt-newintopic,
.new-newintopic,
.new-alt-newintopic,
.topicsubject,
.alt-topicsubject,
.new-topicsubject,
.new-alt-topicsubject,
.topicreplies,
.alt-topicreplies,
.new-topicreplies,
.new-alt-topicreplies,
.topicviews,
.alt-topicviews,
.new-topicviews,
.new-alt-topicviews,
.topictime,
.alt-topictime,
.new-topictime,
.new-alt-topictime {
    color: #616161;
    border-bottom: 1px solid #ccc;
}
.globalmodname {
    color: #F44336;
    font-weight: bold;
}
.author-content,
.post-content,
.subjecttable {
    background: #ffffffa8;
    border-left: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
}
.post_top_link,
.post-options {
    background: #fff;
}
.post_top_link {
    border-left: 1px solid #ccc;
}
.alt-2,
.alt-1,
.footer,
.popup_menu_content,
.popup_menu_highlight,
.markup_panel {
    background: #ccccccf8;
}
}
