}

body#root-page-mobile header#pageheader #profile-extended .profile-links .arrow-top {
    border-color: #0567d6 transparent transparent;
}
#mobile-page #profile-page .section-top .avatar-image .image {
    border: 6px solid #001ca0;
}
#mobile-page #profile-page .section-top .progression .level {
    background-color: #001ca0;
}

body#root-page-mobile header#pageheader nav.menu>ol>li a {
    color: #ffffff;
}

#root-page-mobile header#pagesubheader ul.submenu>li a {
    color: hsl(0, 0%, 77%);
}
#root-page-mobile header#pagesubheader ul.submenu>li.active a {
