}
#root-page-mobile header#pageheader #meta-nav>li.profile-credits .xp-level a .xp {
    color: hsl(0, 0%, 100%);
}
body#root-page-mobile header#pageheader #meta-nav>li.profile-notify #header-icon {
    fill: hsl(0, 0%, 100%);
}
#root-page-mobile header#pageheader #meta-nav>li.menu>a {
    color: #ffffff;
}
#profile-news-feed ul.news-feed-thumbs>li.item {
    box-shadow: inset 0 0 5px 0 rgb(130, 130, 130);
    background-color: #ffffff;
}

