style.css:
/* i really want this to be global */

#header {
    background-color: #f7a1d4;
}
.navigation .notification-icon,
.rbx-highlight {
    background: #f7a1d4;
}
#chat-header {
    background: #f7a1d4;
}
.slide-item-container-left,
.profile-avatar-right {
    background: #f7a1d4;
}
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active {
    box-shadow: 0 -4px 0 0 #f1aac5 inset;
}
.forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
    background: #8e5371 !important;
}
.rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button,
.rbx-vip-server-create,
.btn-secondary-xs {
    background-color: #f7a1d4 !important;
    border-color: #f7a1d4;
}
.rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover,
.rbx-vip-server-create:hover,
.btn-secondary-xs:hover {
    background: #f7a1d4 !important;
    border-color: #f7a1d4;
}
#TopAbpContainer,
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left,
.abp-spacer,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
.sponsored-game,
.alert-info,
#mCSB_1_container > ul > li.font-bold.small,
#mCSB_1_container > ul > li.rbx-nav-sponsor {
    display: none !important;
}
#navbar-search-input,
#navbar-search-btn {
    border: 1px solid transparent;
}
.container-header,
#tab-wardrobe,
#characterInventorySearch,
#tab-outfits,
#HomeContainer > div.col-xs-12.home-header > a {
    background: transparent !important;
}
.profile-avatar-left,
#tab-wardrobe,
#characterInventorySearch,
#tab-outfits,
#HomeContainer > div.col-xs-12.home-header > a {
    background: transparent !important;
}
#party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off {
    background: linear-gradient(to bottom, #f1aac5, #f1aac5);
}
#ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4) {
    display: none;
}
#GamesPageLeftColumn {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
}
#GamesListsContainer > div {
    background-color: #f1aac5 !important;
    width: 100% !important;
}
.dialog-container .dialog-message-container .dialog-message {
    background-color: #f7a1d4;
    border: 1px solid #f7a1d4;
    border-radius: 10px;
    color: #fffdfd;
    padding: 6px 8px;
    position: relative;
    margin: 0 8px 0 0;
    float: right;
    word-wrap: break-word;
}
.dialog-container .dialog-header {
    -webkit-box-shadow: 0 1px 1px -1px rgba(25, 25, 25, 0.3);
    box-shadow: 0 1px 1px -1px rgba(25, 25, 25, 0.3);
    background-color: #f7a1d4;
    position: relative;
    z-index: 2020;
}
.chat-windows-header {
    border-top-right-radius: 4px;
    border-top-left-radius: 4px;
    background-color: #f7a1d4;
    color: #fff;
    height: 32px;
}
.section-content {
    background-color: #ffffff45;
    box-shadow: 0 1px 4px 0 rgba(25, 25, 25, 0.3);
    padding: 15px;
    position: relative;
}
.btn-primary,
.btn-primary:link,
.btn-primary:active,
.btn-primary:visited {
    border-color: #f7a1d4;
    background-color: #f7a1d4;
    background-image: none;
    color: white;
}
body {
    background-color: #E3E3E3;
    color: #5d3f53;
    font-family: "Source Sans Pro", Arial, Helvetica, sans-serif;
    font-weight: 300;
    font-size: 16px;
    width: 100%;
    min-width: 320px;
    margin-bottom: 168px;
    overflow-y: scroll;
}
#left-column .StandardBox a,
#mid-column .StandardBox a,
#right-column .StandardBox a {
    color: #b76f9a;
}
.rbx-left-col {
    height: 96%;
    height: calc(100% - 40px);
    background-color: #ffe2f2;
    overflow: hidden;
    box-shadow: 0 0 3px 0 rgba(25, 25, 25, 0.3);
}

body,
.text,
pre {
    font-family: 'Source Sans Pro', Arial, Helvetica, sans-serif;
    color: #0a0505;
    font-size: 14px;
    line-height: 1.428;
}
#left-column .StandardBox {
    background: #e295c647;
    border: 1px solid #ccc;
}
#SearchControls {
    height: 28px;
    clear: both;
    display: block;
    background: #dc95b936;
    border: 1px solid #ecc5e8;
    margin: 0 0 15px 0;
    position: relative;
    padding: 2px 0;
}
.GroupControlsBox {
    border: 1px solid #e6b1db7a;
    background-color: #eaabcc5e;
    margin-bottom: 10px;
}
.btn-primary-lg {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #f9a6d9;
    border-color: #dc96bc;
    color: #fff;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 15px 15px;
    font-size: 21px;
    line-height: 100%;
    border-radius: 5px;
}
#MasterContainer,
.nav-content-inner,
.container-main,
.gutter-ads,
#Body,
#wrap > div.container-main > div {
    background: url(https://preview.ibb.co/bJeTho/ya.png) top center repeat-x #e3e3e3;
}
