    height: 80px;
    margin: 0 !important; }

.HomeChecklist_Article_Link {
    padding: 8px; }

.HomeChecklist_Article_Body {
    flex: unset;
    width: 532px;
    height: 64px;
    padding: 0 12px 0 0; }

/* 新着マーク */
.HomeChecklist_Article_Unread {
    top: 4px;
    height: 8px;
    width: 8px;
    background: red;
    z-index: 1; }

/* 新着マーク 既読 */
.HomeChecklist_Article_Link:visited .HomeChecklist_Article_Unread {
    background: var(--home); }

/* 新着マーク 既読 フォバー */
.HomeChecklist_Article_Link:visited:hover .HomeChecklist_Article_Unread {
    background: var(--hov); }

.HomeChecklist_Article_Body .Author {
    display: block;
    width: 100%; }

.HomeChecklist_Article_Body .Author_Icon {
    top: 18px; }

.HomeChecklist_Article_Body .Author_PrimaryText {
    position: absolute;
    top: 22px;
    left: 62px;
    width: 460px;
    font-size: 1.4rem; }

.HomeChecklist_Article_Body .Author_SecondaryText {
    position: absolute;
    top: 2px;
    left: 180px;
    margin: 0;
    font-size: 1.3rem; }

/* 記事タイトル */
.HomeChecklist_Article_Title {
    margin-top: 0;
    margin-left: 60px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    /* color: #3970b5;*/ }

.HomeChecklist_Article_Title .TapText span {
    display: unset !important; }

.HomeChecklist_Article_Title span[aria-hidden="true"] {
    display: none !important; }

/* 記事タイトル 既読 */
.HomeChecklist_Article_Link:visited .HomeChecklist_Article_Title {
    color: #689cb5;
    /* color: #969; */ }

/* リブログアイコン付きタイトル */
.HomeChecklist_Article_Icon {
    vertical-align: -0.13em;
    line-height: 1em; }

/* 投稿時刻 */
.HomeChecklist_Article_Meta {
    position: absolute;
    top: 2px;
    left: 62px;
    bottom: initial;
    font-size: 1.3rem; }

/* 未読マーク */
.HomeChecklist_Article_Link .HomeChecklist_Article_Meta::before {
    content: "";
    position: absolute;
    top: 2px;
    left: -62px;
    height: 8px;
    width: 8px;
    border-radius: 10px;
    background: #f90; }

/* 既読マーク */
.HomeChecklist_Article_Link:visited .HomeChecklist_Article_Meta::before {
    background: var(--home); }

/* 既読マーク フォバー */
.HomeChecklist_Article_Link:visited:hover .HomeChecklist_Article_Meta::before {
    background: var(--hov); }

/* フォローフィード 記事サムネイル画像 */
.HomeChecklist_Article_Image {
    width: 64px;
    height: 64px;
    z-index: 1; }

.HomeChecklist_Article_Image img {
    width: 100% !important;
    height: auto !important; }

.HomeChecklist_Article_Image div.-noImage.-ready {
    width: 100% !important;
    height: 100% !important;
    border: 1px solid #ccc; }

.HomeChecklist_Article_Image:hover img {
    top: 75px;
    right: 200px;
    margin-right: -100px;
    width: 180px !important;
    outline: 6px solid #fff;
    box-shadow: 0 20px 60px 0 #00000060; }

/* もっと見る 右列 縦方向 =共通= */
.Collection_ReadMore.-LARGE .Collection_ReadMore_Button {
    font-size: 1.4rem;
    line-height: 30px;
    width: 20em; }

/* 最近見たブログ ************************** */
.PcHorizontalScroller_Container {
    width: 624px; }

/* 最近見たブログ リスト項目*/
.HomeBlogHistory_Article_Link:hover {
    opacity: 1;
    background: var(--hov); }

/* 横送りコントローラ */
.PcHorizontalScroller_Control_Button {
    border: 2px solid var(--backg); }

.PcHorizontalScroller_Control.-left {
    left: -20px; }

.PcHorizontalScroller_Control.-right {
    right: -20px; }

/* 人気のハッシュタグ *********************** */
.HomeBlogHashTag_List_Item {
    width: 286px;
    height: 64px;
    padding: 2px 8px; }

.HomeBlogHashTag_List_Item:nth-child(odd) {
    margin-right: 16px; }

.HomeBlogHashTag_List_Item:hover {
    background: var(--hov); }

.HomeBlogHashTag_List_Item_Link {
    padding: 0; }

.HomeBlogHashTag_List_Item_Link:hover {
    opacity: 1; }

/* ハッシュタグをもっとも見る */
.HomeBlogHashTag_More_Link {
    font-size: 1.4rem; }

.HomeBlogHashTag_More_Link:hover {
    opacity: 1;
    font-weight: bold;
    color: #000; }

/* 新着トピックス ************************** */
/* 表示初期高さを指定 */
.HomeBlogFeed {
    min-height: 3200px; }

.HomeBlogFeed .Tap_Transparent:hover {
    opacity: 1;
    color: #000;
    background: var(--hov); }

/* ジャンルを追加・編集 ********** */
.HomeBlogFeed_Genre {
    background: var(--home_dim); }

.HomeBlogFeed_Genre_Text {
    font-size: 12px; }

.HomeBlogFeed_Genre_Button {
    outline: none; }

.HomeBlogFeed_Genre_Button:hover {
    color: #000;
    background-color: var(--home_dim) !important; }

.HomeBlogFeed_Genre_Icon {
    vertical-align: middle; }

.HomeBlogFeed_Genre_Button:hover .HomeBlogFeed_Genre_Icon {
    background-color: var(--hov); }

.HomeBlogFeed_Genre_Follows {
    margin-top: -10px; }

/* 新着トピックス 項目ヘッダー =共通= */
.HomeBlogFeed_Article_Header {
    margin-bottom: 20px;
    height: 1.8rem; }

/* もっと見る 右列 右方向 =共通= */
.HomeBlogFeed_Article_Header_Link:hover {
    font-weight: bold;
    background: transparent !important; }

.HomeBlogFeed_Article_Header_Link_Icon {
    vertical-align: middle; }

/* 新着トピックス タイル項目 ****** */
.HomeBlogFeed_Article_Card {
    margin-right: 6px; }

.HomeBlogFeed_Article_Card.-col_2 {
    width: 306px;
    margin-right: 12px; }

.HomeBlogFeed_Article_Card.-col_2:last-child {
    margin-right: 0; }

.HomeBlogFeed_Article_Card.-col_2 .HomeBlogFeed_Article_Card_Image img {
    width: 306px !important;
    height: auto !important; }

/* 新着トピックス タイル AD =共通= */
.HomeBlogFeed_Article_Ad:hover {
    background: var(--hov); }

/* 新着トピックス タイル AD背景 =共通= */
#adunit {
    background: var(--home); }

/* ビデオトピックス ************* */
.HomeBlogFeed_Article_VideoTopic_Link {
    padding: 4px; }

/* AmebaTV ******************* */
.HomeBlogFeed_Article_Feature {
    padding: 30px 10px; }

.HomeBlogFeed_Article_AbemaTimetable_Program {
    width: 196px;
    margin-right: 6px; }

.HomeBlogFeed_Article_AbemaTimetable_Image img {
    width: 196px !important;
    height: auto !important; }

/* 芸能人・有名人ブログ *********** */
.HomeBlogFeed_Article_BlogRanking {
    width: 196px;
    margin-right: 6px; }

/* 昨日の人気ランキング *********** */
.HomeBlogFeed_Article_TopicsRanking:last-child {
    width: 246px; }

.HomeBlogFeed_Article_TopicsRanking_Item {
    margin-bottom: 2px; }

.HomeBlogFeed_Article_TopicsRanking_Item_Link {
    width: 246px;
    padding: 8px 0 6px; }

/* ホームフッター ******************************** */
.HomeFooter {
    min-width: 1024px;
    background: none; }

.HomeFooter_Content {
    padding: 28px; }

/* フッター ************************************* */
/* フッター全体 */
footer {
    background: #fefefe; }

/* コピーライト */
.AbemaCopyrights_List {
    width: 1024px; }

/* アメーバ 広報 */
.FooterAmeba {
    width: 1024px; }

/* PCフッター */
.PcFooter_Inner {
    width: 1024px; }

.PcFooter_Corporate_Inner {
    width: 1024px; }

/*プッシュ設定  ***************************************** */
/* プッシュ設定ページ 全体 */
.PcSettingLayout {
    margin-bottom: 20px;
    padding: 60px 20px 5px;
    background: var(--home); }

.PcSettingLayout_MenuItem {
    font-size: 1.6rem; }

.PcSettingLayout {
    color: #000; }

.PcSettingLayout_Header {
    font-size: 16px; }

h4.PcSettingLayout_Headline {
    font-size: 1.5rem; }

.PcSettingLayout_FormItem {
    height: 30px; }

.PcSettingLayout_FormItem_Head {
    font-size: 13px;
    width: 360px;
    padding: 3px 8px 0; }

.PcSettingLayout_FormItem_Body {
    padding: 3px 8px 0; }

.PcSettingLayout_RadioItem {
    font-size: 13px; }

.PcSettingLayout_Radio {
    vertical-align: -2px; }

.PcSettingLayout_FormItem:hover {
    background: var(--hov); }

.PcSettingLayout_FormItem:hover .PcSettingLayout_FormItem_Head {
    background: var(--home); }

/* 下部AD */
.PcFooterAd {
    display: none !important; }

footer {
    display: none; }
}

