style.css:
@-moz-document domain("sozcu.com.tr")
{
    /* Tags */
    iframe,
    img[alt="sozcu-banner-1"],

    /* Class */
    ._ligatus_mobile,
    ._pageskin,
    .browse-content,
    .user-pic,
    .sozcu_mast_head-container,
    .goto-bookstory,
    .pageskin {
        display: none;
        visibility: hidden !important;
    }

  	/* Exceptions */
  .tv-single-iframe,
  .e-embed-video iframe,
  .modal-video-content iframe {
      display: block !important;
      visibility: visible !important;
  }
}
