style.css:
@-moz-document domain("gigazine.net") {

.content section h2 a span
{font-size:16px; line-height:19px;}

.cntimage blockquote, .preface, .article
{font-size:0.95em;}

.cntimage .yeartime
{font-size:0.8em;}

#main > #latest,
#main > #sub,
#G_EF,
.rlcontents-s,
#next-prev,
.followbeta,
.items,
footer,
.social-button,
.sbn,
#EndFooter,
.rlcontents,
#Google_BOT,
#main > #latest, #main > #sub, #subtop
{display:none !important;}

#article .cntimage,
.article
{width:65%; margin:0 auto;}

.cntimage img
{width:100%;}

.date time, .catab
{font-size:.75em}

.date
{height:1.1em}

.content section h2 a
{height:3.2em}

.logo svg
{width: 205px; height: 60px;}
}
