style.css:
@-moz-document domain("travis-ci.org"), domain("travis-ci.com") {
    :root {
        --color: #CCC;
        --background: #222;
        --background-light: #444;
        --border: #555;
        --gray: #9D9D9D;
        --green: #39AA56;
        --red: #DB4545;
        --purple: #9977BB;
        --yellow: #EDDE3F;
    }

    aside#left,
    body,
    button,
    footer,
    pre.codedisplay[class*="language-"],
    .build-commit,
    .build-info,
    .build-list,
    .build-list li,
    .build-tiles li,
    .build-tools,
    .dupa,
    .landing,
    .landing .build-flows,
    .loading-page,
    .main,
    .navigation-nested,
    .notice-banner--yellow,
    .option-dropdown,
    .oss-testing,
    .owner-tile,
    .repos-list,
    .row-branch:hover a,
    .tile.started,
    .top,
    .topbar,
    .username,
    .wrapper,
    .wrapper-main,
    #travis-search
    {
        background: var(--background) !important;
    }

    .job-env:hover,
    .navigation-nested a:hover
    {
        background: var(--background-light) !important;
    }

    .branch-row .row-last-build::before,
    .navigation-nested .nav-nested
    {
        background: var(--border) !important;
    }

    .job-lang:hover
    {
        background: inherit !important;
    }

    .build-commit::after,
    .fade-out::after,
    .job-lang:after,
    .job-env:after,
    .owner-info::after,
    .owner-tile:after,
    .profile-header h1::after,
    .row-message::after,
    .row-branch.booting::after,
    .row-branch.canceled::after,
    .row-branch.created::after,
    .row-branch.errored::after,
    .row-branch.failed::after,
    .row-branch.passed::after,
    .row-branch.queued::after,
    .row-branch.received::after,
    .row-branch.started::after,
    .tile-title.booting::after,
    .tile-title.canceled::after,
    .tile-title.created::after,
    .tile-title.errored::after,
    .tile-title.failed::after,
    .tile-title.passed::after,
    .tile-title.queued::after,
    .tile-title.received::after,
    .tile-title.started::after,
    .username span::after
    {
        background: linear-gradient(-90deg,var(--background) 20%,rgba(255,255,255,0) 100%) !important;
    }

    .commit-description::after
    {
        background: linear-gradient(360deg,var(--background) 5%,rgba(255,255,255,0) 100%) !important;
    }

    li.canceled,
    .build-header.canceled,
    .tile.canceled
    {
        background: linear-gradient(to right,var(--gray) 0,var(--gray) 10px,var(--background) 10px,var(--background) 100%) no-repeat !important;
    }

    li.errored,
    li.failed,
    .build-header.errored,
    .build-header.failed,
    .tile.errored,
    .tile.failed
    {
        background: linear-gradient(to right,var(--red) 0,var(--red) 10px,var(--background) 10px,var(--background) 100%) no-repeat !important;
    }

    li.passed,
    .build-header.passed,
    .tile.passed
    {
        background: linear-gradient(to right,var(--green) 0,var(--green) 10px,var(--background) 10px,var(--background) 100%) no-repeat !important;
    }

    li.booting,
    li.created,
    li.queued,
    li.received,
    li.started,
    .build-header.booting,
    .build-header.created,
    .build-header.queued,
    .build-header.received,
    .build-header.started,
    .tile.booting,
    .tile.created,
    .tile.queued,
    .tile.received,
    .tile.started
    {
        background: linear-gradient(to right,var(--yellow) 0,var(--yellow) 8px,var(--background) 8px,var(--background) 100%) no-repeat !important;
    }

    .close-env::after,
    .detail-job-env::after
    {
        background: none !important;
    }

    .switch .switch-inner,
    .switch.active .switch-inner
    {
        background-color: var(--purple);
    }

    .action-button--cancel,
    .action-button--debug,
    .action-button--restart,
    .build-header,
    .jobs-item,
    .navigation-nested,
    .option-display,
    .row-li,
    .search--sidebar .search-inner
    {
        border-color: var(--border) !important;
    }

    .repo-navigation,
    .sidebar-list ul,
    .travistab-nav.travistab-nav--underline,
    .travistab-nav--secondary a
    {
        border-bottom-color: var(--border) !important;
    }

    .option-tofu
    {
        border-left-color: var(--border) !important;
    }

    .build-tiles li,
    .job-number,
    .pr-row .two-line:first-of-type,
    #left
    {
        border-right-color: var(--border) !important;
    }

    .footer
    {
        box-shadow: 0 -2px 3px var(--border) !important;
    }

    .active,
    .profile-page h2,
    .repo-header,
    .repo-title a,
    .repo-title a:hover,
    #navigation-anchor,
    nav a
    {
        color: var(--purple) !important;
    }

    code
    {
        color: var(--color) !important;
        text-shadow: none !important;
    }
}
