style.css:
@-moz-document url-prefix("https://email.seznam.cz/") {
	/* Disable commercials */
	#detail.has-sky .im-sky {
		display: none;
	}
	iframe {
		display: none;
	}
	/* Resize email content div to fill commercial space */
	#detail.has-sky .scrollable {
		padding-right: 24px;
	}

	/* Fit images to screen */
	img {
		max-width: 100%;
	}
}
