}


/* Image Search
 */

.gsc-imageResult .gs-imageResult .gs-text-box .gs-ellipsis {
    overflow: hidden;
    text-align: left;
    text-overflow: ellipsis;
    -o-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    white-space: nowrap;
}

.gsc-imageResult {
    float: left;
    margin-bottom: 1em;
    margin-right: 20px;
}

.gsc-imageResult-column {
    margin-right: 70px;
}

.gs-imageResult {
    overflow: hidden;
}

.gs-imageResult .gs-image-box {
    height: 86px;
    /* default height + 1px border */
    position: relative;
}

.gs-imageResult .gs-text-box {
    text-align: center;
    overflow: hidden;
    height: 4em;
}


/* No padding for image result */

.gs-imageResult div.gs-title,
.gs-imageResult .gs-snippet,
.gs-imageResult .gs-visibleUrl,
.gs-imageResult .gs-visibleUrl-short,
.gs-imageResult .gs-spelling {
    padding: 0;
}

.gs-imageResult .gs-snippet {
    overflow: hidden;
    line-height: 1em;
    white-space: nowrap;
}

.gs-imageResult .gs-visibleUrl {
    overflow: hidden;
    line-height: 1.3em;
    height: 1.3em;
}

.gs-imageResult .gs-size {
    color: #6f6f6f;
}

.gs-imageResult .gs-image-box {
    text-align: center;
    /* center align image in image box */
}

.gs-imageResult .gs-image-box img.gs-image {
    border: none;
}


/* Classic layout related styles */


/* Do not show title for compact layout such as classic and popup */

.gs-imageResult-classic .gs-text-box .gs-title {
    display: none;
}


/* Column layout related styles */

.gs-imageResult-column {
    width: 474px;
    /* image box width (114px) + image box right margin (10px) + text box width (350px) */
    height: 84px;
    /* image box height */
}

.gs-imageResult-column .gs-image-box {
    float: left;
    margin-right: 10px;
    text-align: left;
}

.gs-imageResult-column .gs-text-box {
    float: left;
    width: 350px;
    height: 86px;
    /* image box height */
}

.gsc-imageResult-column .gs-imageResult-column .gs-text-box .gs-title {
    height: auto;
    text-align: left;
    white-space: normal;
    overflow: hidden;
    text-overflow: ellipsis;
    -o-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
}

.gs-imageResult-column .gs-text-box .gs-size {
    display: none;
}


/* Popup layout related styles */


/* No margin, padding or border for popup layout */

.gsc-imageResult.gsc-imageResult-popup,
.gsc-results .gsc-imageResult-popup:hover {
    margin: 0;
    padding: 0;
    border: 0;
}

.gsc-imageResult .gs-imageResult-popup {
    width: auto;
    /* Override the default image result width */
    overflow: visible;
    /* popup box may overflow and should be visible */
    position: relative;
}


/* Instead put margin bottom and right on searcher image thumbnail. */

.gs-imageResult-popup .gs-image-thumbnail-box {
    padding: 5px;
}

.gs-imageResult-popup .gs-image-box {
    width: auto;
}

.gs-imageResult-popup .gs-image-popup-box {
    width: auto;
    position: absolute;
    padding: 10px;
    background: white;
    left: 0;
    top: 0;
    border: 1px solid #dddddd;
    -webkit-box-shadow: 0 4px 16px rgba(0, 0, 0, 0.2);
