/* Foont size for refinements */

.gsc-tabsArea {
    font-size: 11px;
}


/* For searcher tabs */

.gsc-tabsArea > .gsc-tabHeader {
    height: 27px;
}

.gsc-tabsArea > div {
    height: 30px;
    overflow: auto;
}


/* No spacers needed for keneddy refinements */

.gsc-tabsArea .gs-spacer {
    display: none;
}

.gsc-tabsArea .gs-spacer-opera {
    display: none;
}

.gsc-tabsArea {
    margin-top: 12px;
    margin-bottom: 0;
    height: 29px;
