}


/* Inner wrapper for a result */

.gsc-webResult.gsc-result {
    padding: .25em;
    border: 1px solid #fff;
    margin-bottom: 0;
}


/* Inner wrapper for a result */

.cse .gsc-webResult.gsc-result {
    border: 1px solid #fff;
    margin-bottom: 0;
}


/* Wrapper for a result. */

.gsc-webResult .gsc-result {
