style.css:
div button + [data-test="hint-sentence"]:nth-child(2) {
    animation: fadein 5s;
}

@keyframes fadein {
    0%     { opacity: 0; }
    99%    { opacity: .1; }
    100%   { opacity: 1; }
}

div button + [data-test="hint-sentence"]:nth-child(2):hover {
    animation: fadein 20ms;
}
