style.css:
@-moz-document domain("app.terraform.io") {
  *,
  .button.is-dark-purple,
  .button.is-dark-purple.is-outlined,
  .trial-notice {
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
    box-shadow: none !important;
  }

  body,
  .box {
      color: #abb2c0 !important;
  }

  .pill:not(body).is-info,
  .tag:not(body).is-info,
  .trial-label:not(body).is-info,
  .is-info.pill,
  .is-info.trial-label:not(body),
  .tag.is-info,
  .timeline .timeline-item .timeline-icon.is-icon-large.is-info {
      background-color: #61AFEF !important;
  }

  .timeline .timeline-item .timeline-icon.is-icon-large.is-info {
      color: #fff !important;
  }

  .pill:not(body).is-attention,
  .tag:not(body).is-attention,
  .trial-label:not(body).is-attention {
      background-color: #D19965 !important;
  }

  .has-text-attention {
      color: #D19965 !important;
  }

  a,
  .has-text-info {
      color: #65b6f8 !important;
  }

  .navbar a {
      color: #fff !important;
  }

  .tabs li a {
      color: #6a7786 !important;
  }

  .tabs li.is-active a {
      color: #fff !important;
  }

  .ember-application,
  .footer,
  .message,
  tr,
  .table-filters,
  .module-search-block {
      background-color: #272B33 !important;
      color: #abb2c0 !important;
  }

  .box,
  .has-text-info .ember-view,
  .timeline-inline-comment,
  .timeline .timeline-inline-comment.ember-view {
      background-color: #2c323d !important;
  }

  .timeline .timeline-item .timeline-icon {
      outline: 0 !important;
  }

  .has-text-black,
  a.has-text-black:focus,
  a.has-text-black:hover {
      color: #abb2c0 !important;
  }

  .button.is-primary.is-outlined,
  .is-primary.trial-notice,
  .button,
  .trial-notice {
      background-color: #2f1fd0 !important;
      border: 1px solid #281ab3 !important;
      color: #fff !important;
      box-shadow: none !important;
  }

  textarea {
      color: #abb2c0 !important;
  }

  .table thead.is-resizable th:not(:last-child),
  .table thead,
  .table thead th {
      border-color: #485264 !important;
  }

  .panel-heading.is-dark-white,
  .table thead,
  .table tr.is-active,
  .table tr.is-hovered,
  .table tr:hover,
  .basic-dropdown,
  .panel-block.is-dark-white {
      background-color: #2c323d !important;
  }

  .table-filters .tabs.is-toggle li.is-active a {
      background-color: #485264 !important;
      border-color: #576378 !important;
      color: #65738c !important;
  }

  .breadcrumb a,
  .breadcrumb li.is-active a {
      color: #65738c !important;
  }

  .tabs.is-toggle a {
      border-color: #485264 !important;
      color: #485264 !important;
  }

  .tabs.is-toggle a:hover {
    background-color: #485264 !important;
    border-color: #576378 !important;
    color: #65738c !important;
  }

  .table-filters input,
  .input,
  .textarea,
  input,
  textarea {
      border-color: #485264 !important;
      color: #abb2c0 !important;
      background-color: #2c323d !important;
  }

  .table-filters input:placeholder,
  .input:placeholder,
  .textarea:placeholder,
  input:placeholder,
  textarea:placeholder,
  .input::-webkit-input-placeholder,
  .textarea::-webkit-input-placeholder,
  input::-webkit-input-placeholder,
  textarea::-webkit-input-placeholder {
      color: #abb2c0 !important;
  }

  .table td,
  .table th {
      border-color: #485264 !important;
  }

  .content .ember-view {
      background-color: #2c323d !important;
  }

  .timeline .ember-view {
      background-color: #272B33 !important;
  }

  .has-border-top {
     border-top: 1px solid #485264 !important;
  }

  .has-border-bottom {
     border-bottom: 1px solid #485264 !important;
  }

  .timeline .timeline-item.is-timeline-group-title .level:after,
  hr {
      background-color: #485264 !important;
  }

  .timeline .timeline-item .timeline-content .level.box,
  .box {
      box-shadow: 0 0 0 1px #485264 !important;
  }

  .footer {
      border-color: #485264 !important;
  }

  .footer,
  .footer a {
      color: #485264 !important;
  }

  tr .ember-view {
      background-color: none !important;
  }

  .pill:not(body).is-success,
  .tag:not(body).is-success,
  .trial-label:not(body).is-success,
  .timeline .timeline-item .timeline-icon.is-icon-large.is-success {
      background-color: #abdc89 !important;
      color: #54902b !important;
  }

  .has-text-success {
      color: #54902b !important;
  }

  .enterprise-form .field_with_errors input.pill:not(body),
  .enterprise-form .field_with_errors input.tag:not(body),
  .enterprise-form .field_with_errors input.trial-label:not(body),
  .pill:not(body).is-danger,
  .tag:not(body).is-danger,
  .trial-label:not(body).is-danger,
  .enterprise-form .field_with_errors .timeline .timeline-item input.timeline-icon.is-icon-large,
  .timeline .timeline-item .enterprise-form .field_with_errors input.timeline-icon.is-icon-large,
  .timeline .timeline-item .timeline-icon.is-icon-large.is-danger {
      background-color: #DF6B75 !important;
      color: #fff !important;
  }

  .subtitle.is-page-subtitle {
      color: #abb2c0 !important;
  }

  .sheet .sheet-card .sheet-card-foot,
  .sheet .sheet-card .sheet-card-head {
      background-color: #2c323d !important;
      color: #abb2c0 !important;
  }

  .sheet .sheet-card.is-bordered .sheet-card-head,
  .sheet .sheet-card.is-bordered {
      border-color: #5b626f !important;
  }

  .sheet .sheet-card .sheet-card-body {
      background-color: #252a33 !important;
  }

  .breadcrumb.is-dark-grey.is-underlined,
  .bread-crumbs {
      border: 1px solid #5b626f !important;
  }

  .bread-crumbs {
    border-left: 0 !important;
    border-right: 0 !important;
    border-top: 0 !important;
  }

  .breadcrumb.is-dark-grey.is-inverted,
  .breadcrumb.is-dark-grey.trial-notice,
  .bread-crumbs {
      background-color: #2c323d !important;
  }

  .ansi-theme-default .ansi-red-fg,
  .has-text-danger {
      color: #DF6B75 !important;
  }

  .ansi-theme-default .ansi-bright-red-fg {
      color: #BE5046 !important;
  }

  .ansi-theme-default .ansi-yellow-fg {
      color: #e2c08d !important;
  }

  .ansi-theme-default .ansi-bright-yellow-fg {
      color: #f2ce97 !important;
  }

  .ansi-theme-default .ansi-green-fg {
      color: #97C279 !important;
  }

  .ansi-theme-default .ansi-bright-green-fg {
      color: #abdc89 !important;
  }

  .basic-dropdown,
  .content h1,
  .content h2,
  .content h3,
  .content h4,
  .content h5,
  .content h6 {
      color: #abb2c0 !important;
      border-color: #485264 !important;
  }

  .v2-wrapper {
      background-color: #272B33 !important;
  }

  .box.session-box.session-box-bottom,
  .box.session-box.session-box-top {
      border-color: #2c323d !important;
  }

  .box.session-box .session-title,
  .label,
  .title.is-page-title,
  .checkbox,
  .radio {
      color: #abb2c0 !important;
  }

  .title.is-page-title {
    border-color: #485264 !important;
  }

  code,
  html,
  pre {
    background-color: #3e4451 !important;
  }

  code {
    color: #DF6B75 !important;
  }

  .has-background-dark-purple,
  .navbar.is-dark-purple {
    background-color: #2196F3 !important; /* Material Design Blue 500 */
  }

  .sheet .sheet-card .sheet-card-head .sheet-card-control {
    color: #2196F3 !important; /* Material Design Blue 500 */
  }

  .button.is-dark-purple,
  .brand-button .brand-hamburger span,
  .button.is-primary,
  .is-primary.trial-notice,
  .button.is-primary.is-outlined,
  .button,
  .trial-notice {
    background-color: #1565C0 !important; /* Material Design Blue 800 */
    border-color: #0D47A1 !important; /* Material Design Blue 900 */
  }

  .brand-button .brand-hamburger span {
    background-color: #fff !important;
    border-color: #fff !important;
  }

  @media screen and (max-width: 1087px) {
    .navbar-menu {
      background-color: #2c323d !important;
      color: #abb2c0 !important;
      border-color: #abb2c0 !important;
    }
  }

  .panel.is-black a.panel-block.is-active,
  .panel.is-black a.panel-block.is-hovered,
  .panel.is-black a.panel-block:active,
  .panel.is-black a.panel-block:hover,
  .panel.is-black label.panel-block.is-active,
  .panel.is-black label.panel-block.is-hovered,
  .panel.is-black label.panel-block:active,
  .panel.is-black label.panel-block:hover,
  .navbar-burger:hover {
    background-color: #1f87e5 !important; /* Material Design Blue 600 */
    color: #fff !important;
  }

  .tabs.has-sticky-borders li.is-active a {
    border-color: #1f87e5 !important; /* Material Design Blue 600 */
  }
  
  footer svg.image {
      filter: invert(1);
  }

  .icon.is-svg.is-light-purple svg path,
  .icon.is-svg.is-light-purple svg polyline {
    fill: #fff !important;
    stroke: #fff !important;
  }

  .button.is-text,
  .is-text.trial-notice {
    padding: 0 1em;
  }

  .tabs .icon.is-small svg {
    fill: #65738c !important;
  }

  .panel-heading {
    background-color: #485264 !important;
    color: #65738c !important;
  }

  .arrow-top-content::before,
  .arrow-top-content::after {
    border-bottom-color: #485264 !important;
  }

  .max-width-widest {
      max-width: 100rem;
  }

  .expanding-box,
  .ember-application.expanding-box {
    background-color: #2c323d !important;
    border: 1px solid #485264 !important;
  }

  .expanding-box:hover,
  .expanding-box .is-clickable:hover {
    background-color: #2c323d !important;
  }

  .expanding-box,
  .has-text-grey-dark {
    color: #ABB2D1 !important;
  }

  .title {
    color: #65738c !important
  }

  .has-background-white,
  .subtle-alert {
    background-color: #2c323d !important; 
  }
}
