style.css:
@-moz-document domain("docs.python.org") {
body {
	font: /*[[font-size]]*//1.4 "Open Sans", Georgia, Times, "Times New Roman", serif;
	background-color: #0f1419;
	color: #c5c5c5;
}
h1,
h2,
h3,
h4,
h5,
h6 {
	font-family: "Fira Sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
}
table,
td,
tr,
th {
	background-color: inherit;
}
input,
textarea,
select,
button {
	background-color: #191f26;
	color: #c5c5c5;
	border: 1px solid #1f2633;
}
div.body ul>li {
	margin-top: 1em;
}
div.body ul>li>ul>li {
	margin-top: 0.5em;
}
div.related {
	background-color: #191f26;
	color: #c5c5c5;
	border-top: 1px solid #1f2633;
}
div.related a {
	color: #d2991d;
}
div.related a:hover {
	color: #0095C4;
}
div.related:first-child {
	border: 1px solid #1f2633;
}
.inline-search {
	display: inline;
}
form.inline-search input {
	display: inline;
}
form.inline-search input[type="submit"] {
	width: 30px;
}
div.sphinxsidebar {
	background-color: #191f26;
	background-color: #0f1419;
	border-radius: 0;
	line-height: 130%;
	font-size: smaller;
	font-family: sans-serif;
}
div.sphinxsidebar h3,
div.sphinxsidebar h4 {
	border-bottom-color: #2a2c30;
	font-weight: 500;
	color: #2987a6;
	margin-top: 1.5em;
}
div.sphinxsidebar h3 a {
	color: #6b8e23;
}
div.sphinxsidebarwrapper > h3:first-child {
	margin-top: 0.2em;
}
div.sphinxsidebarwrapper > ul > li > ul > li {
	margin-bottom: 0.4em;
}
div.sphinxsidebar a {
	color: #00608f;
}
div.sphinxsidebar a:hover {
	color: #0095C4;
}
div#sidebarbutton {
	background-color: inherit !important;
	border-radius: 0 !important;
}
div#sidebarbutton:hover {
	background-color: #0f1419 !important;
}
form.inline-search input,
div.sphinxsidebar input {
	font-family: 'Lucida Grande', Arial, sans-serif;
	border: 1px solid #1f2633;
	font-size: smaller;
	border-radius: 3px;
	padding: 5px 8px;
	background-color: #191f26;
	color: #c5c5c5;
	box-shadow: 0 0 0 1px #3e4045, 0 0 0 2px transparent;
}
div.sphinxsidebar input[type=text] {
	max-width: 150px;
}
div.document {
	background-color: inherit;
	max-width: /*[[max-page-width]]*/;
	margin: 0 auto;
	border: 1px solid #02060a;
}
div.body {
	max-width: 100%;
	padding: 10px 20px;
	color: inherit;
	background-color: #090d13;
	border-left: 1px solid #02060a;
}
div.body h1,
div.body h2,
div.body h3,
div.body h4,
div.body h5,
div.body h6 {
	color: #d2991d;
	background-color: inherit;
	font-weight: bold;
	border-bottom: 1px solid #1f2633;
}
div.body h2,
div.body h3,
div.body h4,
div.body h5,
div.body h6 {
	margin-top: 2em;
}
div.body p {
	line-height: 1.45em;
	text-align: left;
}
div.body dd p {
	margin: 1em 0 1em 2em;
}
div.body hr {
	border: 0;
	background-color: #ccc;
	height: 1px;
}
div.body pre {
	border: 0px solid #1f2633;
	background-color: #10151c;
	color: #e6e1cf;
	padding: 15px;
	font-family: "Fira Code", "Source Code Pro", Menlo, Monaco, Consolas, "DejaVu Sans Mono", Inconsolata, monospace;
	font-size: 1em;
	line-height: 1.5;
}
span.copybutton {
	padding: 3px 4px 3px 3px;
	border-radius: 2px !important;
	font-size: 80%;
	top: 3px;
	right: 3px;
	color: #dedede !important;
	background-color: rgba(78, 139, 202, 0.2) !important;
	border-color: #1f2633 !important;
}
div.topic {
	background-color: #0c2633;
	border: 1px solid #1f406b;
}
div.body div.admonition,
div.body div.impl-detail {
	text-align: left;
	border-radius: 0;
	margin-left: 2em;
	border-width: 1px 1px 1px 5px;
}
div.body div.admonition.note {
	background-color: #0b1620;
	border-color: #082e51;
}
div.body div.admonition.warning{
	border-color: #f10606;
	background-color: #090d13;
}
div.body div.admonition.seealso {
    border-color: #568206;
    background-color: #090d13;
}
div.body div.admonition > p,
div.body div.impl-detail > p {
	margin: 0;
}

div.body a {
	color: #3d92ff;
}
div.body a:visited {
	color: #0072aa;
}
div.body a:hover {
	color: #0072aa;
}
tt,
code,
pre {
	font-family: monospace, sans-serif;
	font-size: 96.5%;
}
div.body tt,
div.body code {
	border-radius: 1px;
	font-weight: 500;
	color: #d2991d;
	background-color: inherit;
}
div.body tt.descname,
div.body code.descname {
	font-size: 120%;
}
div.body tt.xref,
div.body a tt,
div.body code.xref,
div.body a code {
	font-weight: normal;
}
.versionadded {
	border-radius: 0;
	background: inherit;
	border: none;
	padding: 0;
	border-color: #1b1819;
}
.versionadded p {
	font-weight: bold;
}
.versionadded .versionmodified {
	font-style: normal;
	color: #3d8a0e;
}
.versionchanged {
	border-radius: 0;
	background: inherit;
	border: none;
	padding: 0;
	border-color: #1b1819;
}
.versionchanged p {
	font-weight: bold;
}
.versionchanged .versionmodified {
	font-style: normal;
	color: #bb860b;
}
.deprecated {
	border-radius: 0;
	background: inherit;
	border: none;
	padding: 0;
	border-color: #1b1819;
	color: #ec0000;
}
.deprecated p {
	font-weight: bold;
}
.deprecated .versionmodified {
	font-style: normal;
}
table.docutils {
	border: 1px solid #ddd;
	min-width: 20%;
	max-width: 90%;
	border-radius: 3px;
	margin: 10px auto;
}
table.docutils td,
table.docutils th {
	border: 1px solid #1d242b !important;
	border-radius: 3px;
}
table p,
table li {
	text-align: left !important;
}
table.docutils th {
	background-color: inherit;
	padding: 0.3em 0.5em;
}
table.docutils td {
	background-color: inherit;
	padding: 0.3em 0.5em;
}
table.footnote,
table.footnote td {
	border: 0 !important;
}
div.footer {
	line-height: 150%;
	margin-top: -2em;
	text-align: right;
	width: auto;
	margin-right: 10px;
}
div.footer a:hover {
	color: #0095C4;
}
.refcount {
	color: #060;
}
.stableabi {
	color: #229;
}
.highlight {
	background: none !important;
}
dt:target,
span.highlighted {
	background-color: #0b1620;
	border-color: #082e51;
}
.section > dl {
	margin-top: 4em;
	margin-bottom: 4em;
}
.section > dl > dt {
	border: 1px solid #2a2c30;
	background-color: #191f26;
	color: #e6e1cf;
	padding: 5px 10px;
	font-size: 1.2em;
	margin-bottom: 1em;
}
.section > dl > dd > dl > dt {
	border: 1px solid #2a2c30;
	background-color: #191f26;
	padding: 5px 10px;
	font-size: 1.2em;
	margin-bottom: 0.5em;
	margin-top: 2em;
}
.section > dl > dt code {
	font-size: inherit;
	font-weight: bold;
	color: #e911ff;
}
.section > dl > dd > dl > dt code {
	font-size: inherit;
	font-weight: bold;
	color: #e911ff;
}
.section > dl dt .descclassname {
	color: inherit;
}
.section > dl dt .sig-paren {
	margin-right: 0.4em;
	margin-left: 0.4em;
}
.section > dl dt .optional {
	margin-right: 0.4em;
	margin-left: 0.4em;
	font-size: inherit;
}
.section > dl dt em {
	font-style: normal;
	font-size: 0.9em;
 	font-weight: bold;
	color: #c5b902;
}
.section > dl em.property {
	color: inherit;
}
.section > dl.exception em.property {
	color: #f6003d;
}
/*Code highlights*/

.notranslate[class^=highlight-] {
	margin: 1em 4em;
	font-size: 0.9em;
}
a.headerlink {
    display: none;
}
}

