/* bm search */
.beatmapsets-search__input {
	background: transparent;
	border-width: 0 0 2px 0;
	border-color: #fc21a4;
	transition-duration: 1s;
}
.beatmapsets-search__input:focus {
	background: rgba(0, 0, 0, .7);
	border-color: #f9c;
	-webkit-box-shadow: none;
	box-shadow: none;
	transition-duration: 1s;
}

/* banner */
.t-section-beatmaps .u-section--gradient-down {
	background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, rgba(68, 17, 136, .9)), color-stop(75%, #418), to(#418)) !important;
	background-image: linear-gradient(rgba(68, 17, 136, .9), #418 75%, #418) !important;
}
.t-section-beatmaps .u-section--bg-normal {
	background-color: #86e !important;
}
.t-section-store .u-section--gradient-down {
	background-image: linear-gradient(rgba(17, 159, 187, .9), #1196bb 75%, #117fbb) !important;
}
.t-section-store .u-section--bg-normal {
	background-color: #66c4ff !important;
}

@keyframes triangles {
	from {
		background-position: bottom;
	}
	to {
		background-position: top;
	}
}
.bg__triangles,
.nav2-header__legacy-triangles {
	animation: triangles 4s forwards;
}

