	box-shadow: none;
}
.header-v3--chat,
.page-extra-tabs-before {
	visibility: hidden !important;
}
.footer {
	background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.55) 40%, rgba(0, 0, 0, 0.85) 100%);
}
.back-to-top {
	opacity: .5;
	transition: 500ms opacity;
}
.back-to-top:hover {
	opacity: 1;
}
.forum-post__info-panel {
