    padding-top: 9px;
    background-image: url(http://store.akamai.steamstatic.com/public/images/v6/maincol_gradient_rule.png);
    background-repeat: no-repeat;
    background-position: bottom left;
    line-height: 26px;
}
h3 {
    text-transform: uppercase;
    color: #fff;
}
a,
a:visited {
