style.css:
@-moz-document domain("memo.cash") {
body {
    margin-left: 270px;
    background: #0F3247;
    color: #D8D8D8;
    max-width: 1000px;
}
.post {
    max-width: 100%;
    box-shadow: 0px 0px 0px;
    border: 0.5px solid #0a2433;
    border-radius: 2px;
    background: #0D2D40;
    color: #D8D8D8;
}
.post .message {
    font-size: 18px;
}

  input[type="text"], input[type="password"], textarea
  {
    background-color: #172f3d !IMPORTANT;
    color: #dedede;
    border-radius: 2px;
  }

a.btn,
input[type=submit].btn {
    display: inline-block;
    padding: 5px 7px;
    border: 1px solid #DA5667;
    color: #DA5667;
    border-radius: 2px;
    background: none;
}
.btn-leave,
.btn a {
    float: right;
    color: #D8D8D8;
}
.post .name {
    margin: 10px 0;
    font-size: 17px;
}
table.table {
    width: 100%;
    background: #0D2D40;
}
table.table a {
    text-decoration: none;
    color: #DA5667;
    border: 0;
    padding: 0;
    padding-right: 20px;
}
table.table .btn {
    float: left;
}
.post a {
    text-decoration: none;
    color: #da5667;
}
.header {
    position: fixed;
    top: 0;
    left: 0;
    height: 100vh;
    max-width: 250px;
    background-image: linear-gradient(18deg, #172F3D 46%, #172F3D 64%);
    z-index: 10;
}
hr {
    display: none;
}
.header a {
    color: #E6E6E6;
    display: block;
    margin: 15px 0px 0px 20px;
    border: 0;
    background: #172F3D;
    font-size: 18px;
}
.header img {
    vertical-align: middle;
    margin-right: 4px;
}
.header h1 a:after {
    content: "Cash";
}
.header h1 a {
    margin-bottom: 20px;
}
}
