style.css:
@-moz-document domain(crackwatch.com) {
    @media only screen and (min-width: 1670px) {
    .game-page-poster {
        position: relative!important;
        top: -70px!important;
        display: inline-block!important;
        width: auto!important;
        height: 75vh!important;
        max-height: 600px!important;
        min-height: 450px!important;
        box-shadow: 0px 2px 14px 0 rgba(0, 0, 0, 0.5)!important;
    }
    .game-page {
        width: 90%!important;
        padding-top: 50px!important;
        min-height: 100vh!important;
    }
    .game-page-left {
        text-align: right!important;
        display: inline-block!important;
        margin-top: 72px!important;
        width: 35%!important;
        vertical-align: top!important;
    }
    .game-page-center {
        text-align: center!important;
        display: inline-block!important;
        width: 30%!important;
        vertical-align: top!important;
        z-index: 11!important;
        position: relative!important;
    }
    .game-page-center > div:first-child {
        background:none!important;
    }
    .game-page-right {
        text-align: left!important;
        display: inline-block!important;
        margin-top: 72px!important;
        width: 20%!important;
        vertical-align: top!important;
    }
    .game-page-header-bottom {
        max-height: 520px!important;
        height: 65vh!important;
        min-height: 350px!important;
    }
    .game-page-header-left,
    .game-page-header-right,
    .game-page-header-center {
        position: relative!important;
        height: auto!important;
        width: 33%!important;
        top: 50px!important;
    }
    .game-page-header-right {
        float: right!important;
    }
    .game-page-header-left > a,
    .game-page-header-right > a,
    .game-page-header-center > a {
        width: 33%!important;
        height: 100%!important;
    }
    .game-page-header-left > a img,
    .game-page-header-right > a img,
    .game-page-header-center > a img {
        max-height: 230px!important;
        display: inline-block!important;
        width: auto!important;
    }
    .game-page-header-bg {
        position: relative!important;
        top: -50px!important;
        width: 100%!important;
    }
}
.game-page >:nth-child(2),
iframe {
    display: none!important;
}
}
