style.css:
@-moz-document domain("plurk.com") {
a[href$="/USERNAME"]:hover:after {
    background: #fff;
    color: #000;
    border: 1px solid #eee;
    border-radius: .25em;
    bottom: 102%;
    left: 0%;
    padding: .25em 1em;
    position: absolute;
    z-index: 999;
    content: "DESCRIPTIVE TEXT";
}

}
