@-moz-document url-prefix(https://www.netflix.com/) {

.PlayerControls--button-control-row {
	border-radius: 9px;
    box-shadow: 0 0 15px rgba(0,0,0,0.6);
    background-color: #222;
    /*border: 3px solid #1a1a1a;*/
}
div.touchable.popup-content-wrapper.active.no-padding div {
	border-radius: 9px;
    box-shadow: none;
    box-shadow: 0 0 15px rgba(0,0,0,0.6);
}
