    box-shadow: none;
}
div.touchable.popup-content-wrapper.active.keep-right div:first-child {
	border-radius: 9px;
    box-shadow: 0 0 15px rgba(0,0,0,0.6);
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(4) div.touchable.popup-content-wrapper.active.keep-right div {
	border: none;
    transition: 0.2s;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(4) div.touchable.popup-content-wrapper.active.keep-right div:hover {
	text-decoration: none;
