	border-radius: 9px;
    box-shadow: none;
    border: none;
    transition: 0.2s;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(6) div.touchable.popup-content-wrapper.active.keep-right div {
	border-radius: 9px;
    box-shadow: none;
    border: none;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(6) div.touchable.popup-content-wrapper.active.keep-right div {
	transition: 0.2s;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(7) div.touchable.popup-content-wrapper.active.keep-right ul:first-child {
	border-right: 1.5px solid #242424;
}
