	border-left: 1.5px solid #242424;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(7) div.touchable.popup-content-wrapper.active.keep-right ul li {
	transition: 0.2s;
    border-radius: 6px;
    margin-left: 7.5px;
    margin-right: 7.5px;
    padding-left: 30px;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(7) div.touchable.popup-content-wrapper.active.keep-right ul li span {
	margin-left: 12px;
    margin-top: 1px;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(6) div.touchable.popup-content-wrapper.active.keep-right ul:first-child {
	border-right: 1.5px solid #222;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(6) div.touchable.popup-content-wrapper.active.keep-right ul:last-child {
	border-left: 1.5px solid #222;
}
.touchable.PlayerControls--control-element.nfp-popup-control:nth-child(6) div.touchable.popup-content-wrapper.active.keep-right ul li {
	transition: 0.2s;
}

.nfp .skip-credits .nf-icon-button, .skip-credits-container-node .skip-credits .nf-icon-button {
	border-radius: 50px;
    font-family: "Google Sans", Arial, sans-serif;
    padding-top: 1px;
    transition: 0.2s;
}
.nfp .skip-credits .nf-icon-button, .skip-credits-container-node .skip-credits .nf-icon-button span {
	padding-left: 3px;
}

.nfp .legacy-controls-styles .scrubber-container .track {
	border-radius: 10em;
    box-shadow: 0 0 15px rgba(0,0,0,0.6);
    /*border: 3px solid #1a1a1a;*/
    background-color: #222;
}
.PlayerControls--progress-control-row-standard {
	margin: 0 5% 0 5%;
    width: 90%;
}
.nfp .legacy-controls-styles .scrubber-container .scrubber-head {
	/*margin-top: -6.3px;*/
}
.nfp .legacy-controls-styles .scrubber-container .buffered {
	background-color: rgba(255,255,255,0.08);
}

.PlayerControls--button-control-row .video-title.text-control h4 {
	color: #bbb;
    font-weight: 600;
}
.PlayerControls--button-control-row .video-title.text-control span {
	color: #888;
    font-weight: 500;
}

.time-remaining--classic .time-remaining__time {
	color: #ddd;
    text-shadow: 0 0 15px rgba(0,0,0,0.7);
    margin-top: -3px;
    margin-right: 4px;
}

.PlayerSpinner--percentage {
	color: #ddd;
    font-size: 12px;
}


button.touchable.PlayerControls--control-element.nfp-button-control.default-control-button.button-bvuiFullScreenOn, button.touchable.PlayerControls--control-element.nfp-button-control.default-control-button.button-bvuiFullScreenOff {
	padding-right: 5px;
}

.PlayerControls--button-control-row .default-control-button {
	transform: scale(0.8);
}

.ReportAProblemPopupContainer+.nfp-popup-control .nfp-button-control {
	border-left: 1.5px solid #222/*!rtl:ignore*/;
    border: none;
    transform: scale(0.8);
}
.nfp .button-reportAProblem {
	border-right: 1.5px solid #222;
    border: none;
    transform: scale(0.8);
}
.nfp .button-reportAProblem .ReportAProblem--button-icon {
	background: #777;
    transition: 0.2s;
}
.nfp .button-reportAProblem:hover .ReportAProblem--button-icon {
	transform: scale(1);
    background: #bbb;
}

.PlayerControls--button-control-row .video-title.text-control {
	border-left: 1.5px solid #222;
    border: none;
}
.PlayerControls--button-control-row .button-volumeLow, .PlayerControls--button-control-row .button-volumeMax, .PlayerControls--button-control-row .button-volumeMedium, .PlayerControls--button-control-row .button-volumeMuted {
	border-left: 1.5px solid #222;
    border-right: 1.5px solid #222;
    border: none;
    transform: scale(0.8);
}
.PlayerControls--button-control-row .button-nfplayerPause, .PlayerControls--button-control-row .button-nfplayerPlay {
	border-right: 1.5px solid #222;
    border: none;
    transform: scale(0.8);
    padding-left: 5px;
}
.PlayerControls--button-control-row .nfp-button-control .svg-icon use {
	fill: #777;
    transition: 0.2s;
}
.PlayerControls--button-control-row .nfp-button-control:hover .svg-icon use {
	fill: #bbb;
}
.PlayerControls--button-control-row .nfp-button-control:hover .svg-icon {
	transform: scale(1);
}

.nfp .legacy-controls-styles .bottom-controls.nfp-control-row .popup-content-wrapper {
	font-family: "Google Sans", Arial, sans-serif;
}

.nfp-button-control.circle-control-button.button-bvuiExit .svg-icon-bvuiExit {
	height: 5.5em;
    width: 5.5em;
}
.nfp-button-control.circle-control-button.button-bvuiExit::after {
    padding-bottom: 11.5px;
    padding-top: 12px;
    border-radius: 9px;
    margin-top: -22.3px;
    box-shadow: 0 0 15px rgba(0,0,0,0.4);
    color: #ddd;
    display: none;
}
.nfp-button-control.circle-control-button.button-bvuiExit::before {
	display: none;
}


.nfp .legacy-controls-styles .trickplay.trickplay-text-and-image {
	border-radius: 9px;
    box-shadow: 0 0 15px rgba(0,0,0,0.4);
}
.nfp .legacy-controls-styles .trickplay.trickplay-text-and-image .tp-image {
    border-radius: 9px;
    border: 6px solid #222;
}
.nfp .legacy-controls-styles .trickplay.trickplay-text-and-image .tp-text {
	background: rgba(20,20,20,0.85);
    width: 20%;
    height: 13px;
    padding-top: 5px;
    padding-bottom: 9px;
    margin-bottom: 4px;
    margin-left: 35%;
    border-radius: 9px 9px 0px 0px;
    margin-top: -33px;
}

.PlayerControls--button-control-row {
	margin: 0 5% 28px 5%;
    width: 90%;
}

.nfp .legacy-controls-styles .scrubber-container .current-progress {
	background: #ad1517;
}
.nfp .legacy-controls-styles .scrubber-container .scrubber-head {
	background: #a01315;
    box-shadow: 0 0 5px rgba(0,0,0,0.5);
}


.WatchNext-still {
	border-radius: 18px;
}

















/* --------- Startseite ---------*/

.bigRowItem .forward-leaning .btn-flat, .bigRowItem .forward-leaning .btn-flat-primary, .bigRowItem .forward-leaning .nf-flat-button, .bigRowItem .forward-leaning .nf-flat-button-primary, .bigRowItem .forward-leaning .nf-icon-button:not(.audio-btn), .billboard-links.forward-leaning .btn-flat, .billboard-links.forward-leaning .btn-flat-primary, .billboard-links.forward-leaning .nf-flat-button, .billboard-links.forward-leaning .nf-flat-button-primary, .billboard-links.forward-leaning .nf-icon-button:not(.audio-btn), .content-refresh .forward-leaning .btn-flat, .content-refresh .forward-leaning .btn-flat-primary, .content-refresh .forward-leaning .nf-flat-button, .content-refresh .forward-leaning .nf-flat-button-primary, .content-refresh .forward-leaning .nf-icon-button:not(.audio-btn), .profiles-billboard .forward-leaning .btn-flat, .profiles-billboard .forward-leaning .btn-flat-primary, .profiles-billboard .forward-leaning .nf-flat-button, .profiles-billboard .forward-leaning .nf-flat-button-primary, .profiles-billboard .forward-leaning .nf-icon-button:not(.audio-btn) {
	border-radius: 50px;
    padding-bottom: 9px;
    padding-top: 11px;
    transition: 0.2s;
}

div.searchInput {
	border-radius: 50px;
    padding-left: 4px;
    padding-right: 4px;
}

div.sub-menu.theme-lakira div li:last-child {
    border-radius: 15px !important;
    transition: 0.2s;
}
div.sub-menu.theme-lakira {
	border-top: none !important;
    border-radius: 15px !important;
    margin-right: -3px;
    margin-top: -2px;
    background-color: rgba(0,0,0,0.7);
    border: 1px solid rgba(200,200,200,0.5) !important;
}
div.sub-menu.theme-lakira div {
	opacity: 1;
}
div.sub-menu.theme-lakira ul li div {
	opacity: 1;
}
div.sub-menu.theme-lakira ul {
	border-radius: 15px;
}
div.sub-menu.theme-lakira ul div {
	border-radius: 15px !important;
}
.akira-header .account-dropdown-button .profile-icon, .pinning-header .account-dropdown-button .profile-icon {
	border-radius: 80px;
    transform: scale(0.9);
}

.title-artwork, .video-artwork {
	border-radius: 8px;
}
.title-card .video-preload-title {
	border-radius: 8px;
}

div.bob-card {
	border-radius: 13px;
