	border-radius: 10px;
}

.maturity-rating .maturity-number {
	border-radius: 50px;
    padding: 1.5px;
    padding-bottom: 2px;
    padding-left: 6px;
    padding-right: 7px;
}

li.current span {
	border-radius: 50px;
}


.jawBonePane .nfDropDown.theme-lakira .label {
	border-radius: 13px;
    transition: 0.2s;
}
.jawBonePane .nfDropDown.theme-lakira .label:focus {
	outline: none;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
}
