	border-radius: 12px !important;
}
.jawBonePane .nfDropDown.theme-lakira .sub-menu {
	border: 1.1px solid #ccc !important;
    border-radius: 12px !important;
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px !important;
    background: rgba(0,0,0,0.85);
    margin-top: -0.5px;
}
.jawBonePane .nfDropDown.theme-lakira .sub-menu a {
	transition: 0.2s;
    font-size: 13px;
    margin-left: -10px;
    padding-left: 10px !important;
    border-radius: 0px;
    width: 110%;
}
.jawBonePane .nfDropDown.theme-lakira .sub-menu a:hover {
	text-decoration: none;
    background: rgba(255,255,255,0.15);
}
.nfDropDown.theme-lakira .label {
	letter-spacing: 0.2px;
}


.member-footer .member-footer-link {
	transition: 0.2s;
}
.member-footer .member-footer-link:hover {
	text-decoration: none;
    color: white;
}
.member-footer .service-code {
	border-radius: 50px;
