style.css:
@-moz-document domain("yex.tv") {
/*Background noir pour les "menus"*/
.backgroundYexBlue, .backgroundYexBlueImage, .backgroundYexBlueImage, .sections, .card, .channelPageCadreVideoInfos,
.channelPageDetails, .channelPageEvals, .channelPageTitle, .channelPageRightMenu, .channelPageCatButton {
    background-color: #000000;
    color: #ffffff;
}

/*suppression des images de background*/
.zonePrincipale, .backgroundYexBlueImage, .faq {
    background-image: none;
}

/*couleur rouge sur les boutons et lien*/
.btn-primary, .nav-pills .nav-link.active, .nav-pills .show>.nav-linkk, .select2-container--default .select2-results__option--highlighted[aria-selected], .tendancestagsTag>span, .channelPageKeywords > span, .progress-bar {
    background-color: #FF0000;
    border-color: #FF0000;
}

/*gris foncé pour le background des pages*/
.tout, .zonePrincipale, .faq {
    color: #ffffff;
    background-color: #333333;
}

/*texte noir pour les "petites" description*/
.channel-reveal {
    color: #000000;
}

/*couleur des liens rouge et plus foncé en hover*/
a, .lienInterne, .grandtitre, .petittitre, .channel-img-icon:hover,
.iconfavori:hover, .iconasuivre:hover, .iconmasque:hover{
    color: #FF0000!important;
}

a:hover, .lienInterne:hover {
    color: #9e0101!important;
}

.btn-primary:hover{
    background-color: #9e0101;
    border-color: #9e0101;
}

/*modification de la couleur des bulles d'alerte*/
.alert-primary {
    color: #fff;
    background-color: #0053a9;
    border-color: #0053a9;
}

.alert-success {
    color: #fff;
    background-color: #006d1a;
    border-color: #006d1a;
}

/*changement des couleurs des boutons YT tv, a decouv, ect ...*/
.tabDecouvrir.active, .tabTV.active, .favoriOK, .tabFavoris.active,
.asuivreOK, .tabAsuivre.active, .masqueOK, .tabMasques.active, 
.tabTous.active, .tabMonYex.active {
    color: #fff!important;
}

.tabDecouvrir:hover, .tabTV:hover, .tabFavoris:hover, .tabAsuivre:hover,
.tabMasques:hover, .tabTous:hover, .tabMonYex:hover {
    color: #9e0101!important;
}

/*Page FAQ*/
.faq p, .faq .petittitre, .faq .lienInterne{
    text-align: left;
    padding-left: 10%;
    margin-left: 0;
    margin-right: 0;
}
  
}
