style.css:
@-moz-document url-prefix("https://www.douyu.com"), url-prefix("http://www.douyu.com") {
/* 基础优化 */
.feedback-e27241,
.is-normalRoom.guessGameContainer,
.FansMedalDialog-normal,
.bc-wrapper.bc-wrapper-18,
.answerProblem-35fd97,
.PkView,
.Category.public-DropMenu
{
	display: none !important;
}


/* 左侧栏优化 */
a.Aside-nav-item:nth-of-type(2),
a.Aside-nav-item:nth-of-type(3),
a.Aside-nav-item:nth-of-type(4),
div.Aside-menu-block:nth-of-type(3),
div.Aside-menu-block:nth-of-type(6),
div.Aside-menu-block:nth-of-type(7),
div.Aside-menu-block:nth-of-type(8),
div.Aside-menu-block:nth-of-type(9),
.Aside-footer

{
	display: none !important;
}

/* 导航优化 */
.Broadcast > .public-DropMenu > .public-DropMenu-link,
.Download > .public-DropMenu > .public-DropMenu-link,
.Game.public-DropMenu,
.HeaderNav

{
    display: none !important;
}

/* 直播列表 */
.layout-Cover--withAside-container.layout-Module > .layout-Module-container > .layout-Cover-list

{
    display: none !important;
}

/* 我的关注 */
.Prompt-link,
.layout-Module-head,
li.layout-Cover-item:nth-of-type(1)
{
    display: none !important;
}

/* 直播间去广告 */
.TopButton-btn,
.LuckGiftEnter,
.PlayerToolbar-signCont,
.code_box-5cdf5a,
.micro-cd311c,
.mouse-disable-3cc40a.yanzhi-b079f5,
.is-show.AppFlow
{
	display: none !important;
}

/* 视频框去广告 */
.luckBg1-cb6b60,
.closeBg-998534,
.bg-d4758b,
.mouse-disable-3cc40a.PkView-3fc34f,
.react-draggable.ChargeTask-closeBg,
.PlayFishBallEnter
{
	display: none !important;
}

/* 欢迎XX进入直播间 */
li.jschartli p.my-cont span.m {
    color: #ff5d23!important;
    display: inline-block !important;
}
li.jschartli p.my-cont {
    padding: 7px!important;
    border-radius: 6px!important;
    border: 1px dashed #ff5d23!important;
    background: #141414 !important;
}

/* 播放器水印 */
.watermark-442a18
{
    display: none !important;
}
.Autograph-level,
.Autograph-content{
    border-bottom: none;
    background-color: transparent;
}

/* 直播间宣传图 */
#js-background-holder {
    display: none;
}

/*贡献榜调整*/
.ChatRank {
    height: 60px;
}
.layout-Player-barrage {
    top: 12%;
}
.layout-Player-rank {
    height: auto;
    border-bottom: none;
}
.NobleRankTips-button {
    padding: 0;
}
.bg-icon {
    background-image: none !important;
}
n {
    background-image: none !important;
}

/*聊天区调整*/
.Barrage--paddedBarrage {
    padding-bottom: 0px;
    padding-top: 0px;
}
.Barrage-listItem {
    line-height: 18px;
}


.ChatBarrageCollect
{
	display: none !important;
}


/*背包*/
.PlayerToolbar-Wealth {
    float: right;
    text-align: right;
    color: #777;
    line-height: 1px;
    margin-top: 3px;
    width: 100%;
}
}

@-moz-document url-prefix("https://www.douyu.com"), url-prefix("http://www.douyu.com") {
/* 修复某些直播间框体抖动 */
.layout-Container, .layout-Container--hasAside {
    padding-top: 50px;
}
}

@-moz-document url-prefix("https://www.douyu.com"), url-prefix("http://www.douyu.com") {
/* 直播间活动元素 */
.ActDayPay-entry,
.Act129684Tips,
.TurntableLottery-enter,
.layout-Banner,
.layout-Bottom-forDiy,
.ActDayPay-toast,
.TitleSuperFansIcon,
.RoomLevelDetail,
.FishpondTreasure
{
    display: none !important;
}
}

@-moz-document url-prefix("https://www.douyu.com/topic") {
/* 活动直播间调整 */
#bc6,#bc3 {
    padding-top: 90px !important;
    height: auto !important;
    padding-bottom: 20px !important;
    background-image: none !important;
}

#bc2 {
    margin-top: 0px !important;
}
.wm-h5-view {
    display: none !important;
}

.shark-webp .layout-Container {
    background: #1a1a1a!important;
}





}

@-moz-document url-prefix("https://www.douyu.com") {
.LotteryContainer {
    position: fixed;
    top: 50%;
    left: 89.5%;
    z-index: 1000;
    width: 360px;
    height: 458px;
    background: url(https://sta-op.douyucdn.cn/front-publish/live-master/assets/images/bg_9265ea5.png) 50% no-repeat;
}

.UPlayerLotteryEnter {
    position: absolute;
    top: 87%;
    left: 1%;
    z-index: 100;
    width: 90px;
    height: 100px;
    }
}
