style.css:
@-moz-document url-prefix("https://d.tube") {
a {
	color: #73ba25 !important;
}
a:visited {
	color: #00a489 !important;
}
.ui.red.buttons .button, .ui.red.button {
	background-color: #73ba25;
}
.ui.red.buttons .button, .ui.red.button:hover {
	background-color: #6da741;
}
.icon.sign.in {
	color: #73ba25 !important;
}
.nightmode {
	background: #3a3a3a !important;
}
.nightmodegray {
	background: #222 !important;
}
.ui.large.borderless.main.menu.fixed.nightmode {
	background-color: #636363 !important;
}
.ui.large.borderless.main.menu.fixed.nightmode .menu {
	background-color: #636363 !important;
}
div.nightmode .ui.input input {
	background: #515151 !important;
	border-color: #383838 !important;
	color: #eee;
}
div.nightmode .ui.input input:active {
	background: #515151 !important;
	border-color: #73ba25 !important;
}
.sidebartoggleicon.item.header.nightmodetext .sidebar.icon.nightmodegray {
	background: none !important;
}
#videosnaptags a {
	background-color: #4a4a4a;
	color: #eee !important;
	border-radius: 2px;
}
.sidebarlink.nightmodegray a {
	color: #6da741 !important;
}
#textlogo.nightmodetext:hover {
	background-color: #5a5a5a;
}
.menuitem.dtubesidebarmenu a {
	color: #262626 !important;
}
.menuitem.dtubesidebarmenu .nightsecondarytext {
	color: rgb(180, 180, 180) !important;
}
.home.icon.dtubesidebaricon {
	color: #73ba25;
}
.sidebarlink {
	color: #222 !important;
}
span.sidebarlink {
	color: #6da741 !important;
}
}
