style.css:
@-moz-document domain("vk.com") {
.like_btn.like .like_button_icon {
    background-image: url(https://pp.userapi.com/c845416/v845416546/51e73/Fhb5iDUoGnI.jpg);
}
.like_btn.like.active .like_button_icon {
    background-image: url(https://pp.userapi.com/c845416/v845416546/51e6c/X1-Ca5LdhaQ.jpg);
}
.like_btn.comment .like_button_icon {
    background-image: url(https://pp.userapi.com/c845416/v845416546/51e65/l-VtH1TcfuE.jpg);
}
.like_btn.share .like_button_icon {
    background-image: url(https://pp.userapi.com/c845416/v845416546/51e5e/ipxry8-YxCg.jpg);
}
.like_wrap.lite .like_btn.like .like_button_icon {
    background-image: url(https://pp.userapi.com/c845416/v845416546/51e73/Fhb5iDUoGnI.jpg);
    opacity: 0.48;
}
.like_wrap.lite .like_btn.like.active .like_button_icon {
    background-image: url(https://pp.userapi.com/c845416/v845416546/51e6c/X1-Ca5LdhaQ.jpg);
    opacity: 1!important;
}
.like_button_count {
    color: #000000;
    font-size: 12px;
    font-weight: 400;
}
.like_btn.like.active .like_button_count {
    color: #000000;
}
.like_button_icon {
    width: 20px;
    height: 20px;
    background: no-repeat 50% / contain;
    opacity: 0.7;
    -o-transition: opacity 100ms ease-in-out;
    transition: opacity 100ms ease-in-out;
    -webkit-flex: 0 0 auto;
    -ms-flex: 0 0 auto;
    flex: 0 0 auto;
}
}
