/* ==UserStyle==
@name           privatebin - userCSS
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    reading mode for privatebins
@author         serjflint <serjflint@gmail.com> (https://github.com/serjflint)
@license        CC-BY-4.0
@preprocessor   default
@homepageURL    https://github.com/serjflint/privatebin-reader_view
@supportURL     https://github.com/serjflint/privatebin-reader_view/issues
@updateURL      https://github.com/serjflint/privatebin-reader_view/blob/master/reader_view.user.css
==/UserStyle== */
@-moz-document domain("paste.imirhil.fr"),
domain("privatebin.net"),
domain("paste.fizi.ca"),
domain("paste.tech-port.de"),
domain("priv.atebin.com"),
domain("privatebin.secured.fi") {
	html {
		font-size: 16px;
	}
	body {
		background-color: rgb(246, 246, 246);
		color: rgb(17, 17, 17);
		font-family: Helvetica, Arial, sans-serif;
		font-size: 1.5em;
		line-height: 1.5;
		writing-mode: unset;
		max-width: 1050px;
		height: unset;
		font-style: normal;
		font-weight: 400;
		margin: 0 auto;
		padding: 40px 10px 80px;
	}
	body * {
		color: rgb(17, 17, 17);
		background-color: transparent;
	}
	section.container,
	article.row,
	pre,
	div#cleartext,
	div#plaintext,
	div.well,
	main {
		width: inherit;
		margin: 0;
		padding: 0;
		max-width: 100%;
		white-space: normal;
		vertical-align: baseline;
		box-shadow: none;
		border: none;
		background-color: inherit;
	}
	div.center-block {
		width: inherit;
		max-width: 100%;
	}
	.input-group {
		float: none;
		padding-left: 0;
		padding-right: 0;
		display: table;
	}
	.input-group.text-justify.center-block:first-child .well p {
		margin: 0 auto;
		text-align: center;
	}
	#plaintext p {
		margin: 1.5em 0;
	}
	nav,
	footer,
	#remainingtime {
		display: none;
		visibility: hidden;
	}
	h1,
	h2,
	h3,
	h4,
	h5,
	h6 {
		font-weight: 700;
		font-size: 1.8em;
		line-height: 1.35;
		display: block;
		margin-block-start: 0.67em;
		margin-block-end: 0.67em;
		margin-inline-start: 0px;
		margin-inline-end: 0px;
	}
	.btn {
		display: inline-block;
		margin-bottom: 0;
		font-weight: normal;
		text-align: center;
		vertical-align: middle;
		cursor: pointer;
		background-image: none;
		border: 1px solid transparent;
		white-space: nowrap;
		padding: 6px 12px;
		font-size: inherit;
		line-height: 1.42857143;
		border-radius: 4px;
	}
	.btn-primary {
		color: #fff;
		background-color: #337ab7;
		border-color: #2e6da4;
	}
}
