style.css:
@-moz-document domain("adnmb2.com"),domain("adnmb1.com"), domain("tnmb.org") {
html {
    color: #8E8E8E !important;
    /*背景字体颜色*/
    background: #252525 !important;
    /*背景颜色*/
}
#h-menu {
    border-right-color: #8E8E8E !important;
    overflow: hidden !important;
    width: 150px;
    background: #303030;
    /*侧边背景颜色*/
    background-repeat: no-repeat;
    background-position: 50% 1%;
    box-shadow: 0px 0px 100px  rgba(0,0,0,.2);
    border-style: none;
}
#h-menu-top-img img{
    transition:opacity .3s ease,box-shadow .3s ease;
    border-radius:24px;
    opacity:.2;
}
#h-menu-top-img img:hover{
    transition:opacity .3s ease,box-shadow .3s ease;
    opacity:.8;
    box-shadow: 0px 3px 20px  rgba(0,0,0,.2);
}
#h-menu-top-sitename:before {
    font-size: 16px;
    content: "黑丝岛-JK匿名版\A";
    color: #8E8E8E;
    /* 岛字体颜色 */
}
#h-menu-top-sitename {
    width: 120px;
    max-height: 20px;
    /* 请修改为第一行的高度 */
    overflow: hidden;
}
#h-menu-search-keyword {
    transition:background .3s ease,box-shadow .3s ease;
    height: 20px;
    width: 100px !important;
    background-color: #464646;

    border-radius:1px;
    box-shadow: 0px 3px 20px  rgba(0,0,0,.2);
    color: #8E8E8E;
    border-style: none;
    padding: 0px 8px;
}
#h-menu-search-keyword:hover {
    transition:background .3s ease,box-shadow .3s ease;
    background-color: #505050;
    color: #8E8E8E;
}
#h-menu-content {
    color: #8E8E8E;
    /* 侧边字体颜色 */
}
.h-nav-parent-header,
.h-nav-header,
h2.h-title,
span.h-threads-info-title,
font[color$="#789922"] {
    /*引用颜色*/
    color:#bbb !important;
    /*标题颜色*/
}
.h-nav-item,
.uk-nav-sub>li>a {
    text-indent: 10px;
    color: #8E8E8E;
    /* 侧边字体颜色 */
}
.h-active,
.uk-active>span {
    background: #464646 !important;
    /*侧边高亮颜色*/
}
.h-disabled,
.uk-disabled>span {
    background: #353535 !important;
    color:rgba(0,0,0,0) !important;
}

.uk-pagination>li>a {
    color: rgba(0,0,0,0);
    background: #606060;
    box-shadow: 0px 5px 5px  rgba(20,20,20,.2),0px 0px 10px  rgba(0,0,0,.1);
}
.uk-container-center,
.system-message {
    background-color: #252525 !important;
    /*公告底色*/
}
.uk-container-center>div {
    color: #8E8E8E !important;
    /*公告字体色*/
}
#h-bottom-nav,
.h-tool-btn {
    background: rgba(55,55,55,.95) !important;
    /*底栏颜色*/
    color: #e0e0e0 !important;
}
#h-bottom-nav {
    border-style: none;
    padding: 16px 24px;
}
span.h-threads-info-email {
    color: #6e6e6e !important;
    /*名字颜色*/
}
div.h-threads-item-reply-main {
    transition:background .3s ease,box-shadow .3s ease;
   /* background: #464646 !important;;*/
    background: rgba(70,70,70,1) !important;
    /*卡片背景颜色*/
    box-shadow: 0px 5px 5px  rgba(20,20,20,.2),0px 0px 10px  rgba(0,0,0,.1);
    border-radius:1px;
    padding:8px 12px 10px;
    border-style: none;
}
div.h-threads-item-reply-main:hover {
    transition:background .3s ease,box-shadow .3s ease;
    background: rgba(80,80,80,1) !important;
    /*鼠标悬停卡片背景颜色*/
    box-shadow: 0px 10px 50px  rgba(20,20,20,.3),0px 5px 30px  rgba(0,0,0,.2);
}
div.h-threads-item-reply {
    margin: 15px 0px 0px 0px;
    border-radius:10px;
}
.h-post-form-title {
    background: #363636 !important;
    /*公告侧栏颜色*/
    color: #8E8E8E !important;
    /*公告侧栏字体颜色*/
    margin: 4px 0px 0px 0px;
}
.h-post-form-option input{
    transition:background .3s ease,box-shadow .3s ease;
    color: #8e8e8e!important;
    background-color: #464646!important;
    border-radius:3px;
    box-shadow: 0px 2px 10px  rgba(0,0,0,.4) ;
    border-style: none;
    padding: 4px 8px 3px;
    margin: 2px 0px 0px 0px;
}
.h-post-form-option input:hover{
    transition:background-color .3s ease,box-shadow .3s ease;
    background-color: #505050!important;
    box-shadow: 0px 5px 30px  rgba(0,0,0,.6) ;
}
.h-post-form-input input{
    transition:background .3s ease,box-shadow .3s ease;
    color: #8e8e8e!important;
    background-color: #464646!important;
    border-radius:1px;
    box-shadow: 0px 2px 10px  rgba(0,0,0,.4) ;
    border-style: none;
    padding: 5px 0px 4px;
    margin: 2px 0px 0px 0px;
}
.h-post-form-input input:hover{
    transition:background-color .3s ease,box-shadow .3s ease;
    background-color: #505050!important;
    box-shadow: 0px 5px 30px  rgba(0,0,0,.6) ;
}
#h-emot-select{
    transition:background .3s ease,box-shadow .3s ease;
    color: #8e8e8e;
    background-color: #464646;
    border-radius:1px;
    box-shadow: 0px 2px 10px  rgba(0,0,0,.4) ;
    padding: 4px 0px 3px;
    margin: 2px 0px 0px 0px;
    border-style: none;
}
#h-emot-select:hover{
    transition:background-color .3s ease,box-shadow .3s ease;
    background-color: #505050;
    box-shadow: 0px 5px 30px  rgba(0,0,0,.6) ;
}
.h-post-form-textarea{
    transition:background .3s ease,box-shadow .3s ease;
    color: #8e8e8e;
    background-color: #464646;
    border-radius:1px;
    box-shadow: 0px 2px 10px  rgba(0,0,0,.4) ;
    margin: 2px 0px 0px 0px;
    border-style: none;
    padding: 16px 16px;
}
.h-post-form-textarea:hover{
    transition:background-color .3s ease,box-shadow .3s ease;
    background-color: #505050;
    box-shadow: 0px 5px 30px  rgba(0,0,0,.6) ;
}
.h-threads-item-reply-icon{
    visibility:hidden;
}
#h-post-form{
    background-color: #2e2e2e !important;
    border-radius:6px;
    box-shadow: 0px 2px 10px  rgba(0,0,0,.4) ;
    padding: 5px 20px;
    width:80%;
    max-width:500px;
}
.uk-breadcrumb{
    color: #8e8e8e !important;
    background-color: #464646;
    border-radius:6px;
    box-shadow: 0px 2px 10px  rgba(0,0,0,.4) ;
    max-width: 400px;
    margin: 15px 0px 15px 0px;
    padding: 4px 8px;
}
.h-title{
    font-size:20px;
    margin: -5px 0px;
}
.h-threads-item-main{
    margin: 10px 0px 5px 0px;
}
hr {
    border-top:1px solid rgba(70,70,70,1);
}
.h-threads-info{
    background-color: rgba(37,37,37,.2);
    font-size: 10px !important;
    color: #6e6e6e !important;
    border-radius: 3px;
    margin: -10px -20px 0px -20px;
    padding: 8px 20px;
}
.uk-text-primary {
    background-color: #8e8e8e;
    /*po主高亮底色*/
    color: #464646 !important;
    border-radius: 8px;
    margin: 2px;
    padding: 1px 5px;
}
.h-threads-info-uid>font>sub {
    display: none;
}
a:hover{
    transition:color .3s ease,text-shadow .3s ease;
    text-decoration: none;
    color:#FFF!important;
    text-shadow:0px 0px 3px #000,0px 0px 5px #000,0px 0px 5px #fff,0px 0px 5px rgba(255,255,255,.5)!important;
}
a:hover *{
    transition:color .3s ease;
    text-decoration: none;
    color:#FFF!important;
}
a:link {
    transition:color .3s ease,text-shadow .3s ease;
    color:rgba(23,164,239,1);
    text-decoration: none;
} 
a:visited{
    transition:color .3s ease,text-shadow .3s ease;
    color:rgba(23,164,239,1);
    text-decoration: none;
    text-shadow: 0px 2px 5px #333,0px 0px 10px #333;
}
::selection{
    background-color:#ccc!important;
    color:#000;
}
.h-threads-img-tool-btn{
    background-color: #2c2c2c !important;
    font-size: 12px !important;
    border-radius: 3px;
    margin: 0px 2px;
    padding: 4px 6px;
}
.h-threads-img-tool-btn:hover{
    transition:background-color .3s ease,box-shadow .3s ease;
    background-color: #363636 !important;
    box-shadow:  0px 10px 50px  rgba(20,20,20,.3),0px 5px 30px  rgba(0,0,0,.2);
}
.h-threads-img-tool{
    margin: -5px 2px 3px 2px;
    padding: 6px 6px;
    max-width:370px;
}
.h-threads-img-a img{
    transition:box-shadow .3s ease;
    border-radius:3px;
    box-shadow: 0px 5px 5px  rgba(20,20,20,.2),0px 0px 10px  rgba(0,0,0,.1);
}
.h-threads-img-a img:hover{
    transition:box-shadow .3s ease;
    box-shadow:  0px 10px 50px  rgba(20,20,20,.3),0px 5px 30px  rgba(0,0,0,.2);
}
}
