style.css:
@-moz-document regexp("https?:\\/\\/rsload\\.net\\/.*") {
body {
    background-color: black !important;
    color: white !important;
}
.header {
    background-image: linear-gradient(to bottom, gray 0%, black 100%) !important;
}
.header::before,
.side-bt,
.short-top,
.comm-one,
.footer {
    background: linear-gradient(to bottom, gray 0%, black 100%) !important;
    color: #FFF;
}
a {
    color: white !important;
}
.side-bc,
.login-box,
.last-comm-link,
.lb-user,
li > a,
ul,
.lb-menu,
.icon-l,
.static-page,
.lc-popup,
.lc-text,
.ui-dialog-titlebar,
.ui-widget-header,
.pm tr:nth-child(1),
.form-wrap,
.form-textarea > label {
    background-color: gray !important;
}
.short-text,
.full-text,
.video-box,
#story,
#login_name,
#login_password,
.lb-check input + label::before,
.fr-view,
.sres-wrap,
#searchinput,
.pm-page,
.form-item > input[type="text"],
.form-item > input[type="password"],
.form-item > input[type="file"],
input[name="question_answer"],
textarea {
    background-color: silver !important;
    color: black !important;
}
.fa-gear::before,
.fa-cog::before,
.fa,
.fa::before,
.fa-heart-o,
.fa-heart-o::before {
    background-color: transparent !important;
}
a::hover {
    text-decoration-color: white !important;
}
.lb-menu a,
.side-top a,
.lcomm {
    border-bottom-color: silver !important;
}
.button,
.oplus,
.orating,
.pagi-load a,
.up-second li a,
.up-edit a,
.qq-upload-button,
button:not(.color-btn),
html input[type="button"],
input[type="reset"],
input[type="submit"] {
    background: linear-gradient(to bottom, #999 0%, #333 100%) !important;
    border: none !important;
}
.oplus {
    background: linear-gradient(to bottom, #BBB 0%, #111 100%) !important;
    border: none !important;
    box-shadow: none !important;
}
.ominus {
    background: linear-gradient(to bottom, #CCC 0%, #222 100%) !important;
    border: none !important;
    box-shadow: none !important;
}
.ogold {
    background: linear-gradient(to bottom, #DDD 0%, #AAA 100%) !important;
    border: none !important;
    box-shadow: none !important;
}
#gotop {
    background-color: black !important;
}
a.logotype > img,
div#dle-content > center,
article.full > div > div:nth-child(2) > center > a > img {
    display: none !important;
}
a.logotype:before {
    content: URL('https://i.ibb.co/Hq6wm1P/logo-virus.png') !important;
}
a.orating_m {
    background: URL('https://i.ibb.co/n7qF4B7/oicons.png') !important;
    background-position: 12px 0 !important;
}
a.orating_p {
    background: URL('https://i.ibb.co/n7qF4B7/oicons.png') !important;
    background-position: 24px 0 !important;
}
.short-title .fa,
.header-menu .fa {
    color: silver !important;
}
.lc-popup {
    border-color: black !important;
}
div.lcomm:hover,
a.lc-title:hover,
.lc-author > b > a:hover,
.comm-two,
.comm-body {
    background-color: silver !important;
}
a.lc-title:hover,
.lc-author > b > a:hover {
    text-decoration: underline !important;
}
.navigation a:hover,
.pnext > a:nth-child(1):hover,
.pprev > a:nth-child(1):hover {
    background-color: #666 !important;
}
.navigation span,
div.pagi-nav {
    background-color: #444 !important;
}
.navigation,
span.nav_ext,
.fr-counter {
    background-color: silver !important;
    color: #333 !important;
}
.navigation a {
    color: #333 !important;
}
.counters > a:nth-last-child(2) > img:nth-child(1),
.counters > a:nth-last-child(1) > img:nth-child(1),
div.title_spoiler > a:first-child > img:first-child,
div.title_spoiler > img:first-child {
    display: none !important;
}
/*.counters > a:nth-last-child(2):before {
    content: URL('http://i105.fastpic.ru/big/2018/0517/8e/ca4034ae2c9396d4ad77d677e080378e.png') !important;
}
.counters > a:nth-last-child(1):before {
    content: URL('http://i105.fastpic.ru/big/2018/0517/2c/226df220673b9b0a759cff0ed7dc072c.png') !important;
}*/
div.comm-item > div.comm-left div:nth-last-child(2) > img,
img[src="/templates/rsload-2016/dleimages/noavatar.png"],
div.comm-av > img,
img.emoji,
.up-third > li:nth-child(3) > img:nth-child(2),
#lb-ava img,
.comm-left > div:nth-child(6) > img:nth-child(1) {
    -webkit-filter: grayscale(1) !important;
    filter: grayscale(1) !important;
}
div.comm-av > img:not([src="/templates/rsload-2016/dleimages/noavatar.png"]):hover,
#lb-ava img:hover {
    -webkit-filter: none !important;
    filter: none !important;
}
/*div.title_spoiler>a:first-child:before,*/
.title_spoiler:before {
    content: URL('https://i.ibb.co/ZdrymXg/spoiler-minus.png') !important;
}
.title_spoiler a img {

    content: URL('https://i.ibb.co/f8FwQq8/spoiler-plus.png') !important;
}
article.full div div > a {
    color: #333 !important;
}
.comm-rate2 .fa,
.up-third > li:nth-child(4) > a:nth-child(1) {
    color: #333 !important;
    background-color: transparent !important;
}
div.comm-rate2 a {
    background-color: #666 !important;
}
div.comm-rate2 a:hover {
    background-color: #777 !important;
}
div.title_spoiler {
    background-color: #666 !important;
}
div.text_spoiler,
div.quote {
    background-color: #DDD !important;
}
.title_quote,
.berrors {
    background-color: #AAA !important;
}
.comments-tree-list > .comments-tree-item > .comments-tree-list,
.search-page,
.up-first {
    background-color: gray !important;
    color: white !important;
}
span[style="color:red"] {
    /*Администраторы*/
    color: #000 !important;
}
span[style="color:#779999"] {
    /*Легенда*/
    color: #111 !important;
}
span[style="color:#aa55aa"] {
    /*Ветеран*/
    color: #222 !important;
}
span[style="color:#00aa99"] {
    /*Живущий здесь*/
    color: #333 !important;
}
span[style="color:#bb7722"] {
    /*Душа компании*/
    color: #444 !important;
}
span[style="color:#cc6600"] {
    /*Завсегдатай*/
    color: #555 !important;
}
span[style="color:#2A17B1"] {
    /*Активист*/
    color: #666 !important;
}
span[style="color:black"] {
    /*Заглянувший*/
    color: #CCC !important;
}
span[style="color:#A17B7B"] {
    /*Downloader*/
    color: #444 !important;
}
span[style="color:green"] {
    /*Друг сайта*/
    color: #666 !important;
}
.ui-widget-content,
.ui-dialog-content,
.fr-popup,
.fr-link-attr,
.fr-not-empty {
    background-color: #999 !important;
    color: black !important;
}
.gray-theme.fr-popup .fr-action-buttons button.fr-command,
.gray-theme.fr-popup .fr-action-buttons button.fr-command:hover,
.gray-theme.fr-popup .fr-action-buttons button.fr-command:focus {
    color: white !important;
}
.gray-theme.fr-toolbar,
.up-edit {
    border: none !important;
    background: gray !important;
}
.gray-theme.fr-popup .fr-input-line input[type="text"],
.gray-theme.fr-popup .fr-input-line textarea {
    border: none !important;
    background: gray !important;
}
div.fr-input-line > label {
    display: none !important;
}
.fr-active,
a[itemprop="item"] {
    color: white !important;
}
.sres-text > h2 {
    color: black !important;
    font-weight: bold !important;
}
.sres-text > h2:hover {
    text-decoration: underline !important;
}
.online {
    background-color: #666 !important;
}
.offline {
    background-color: #AAA !important;
}
.up-third li:nth-child(2n),
.pm tr:nth-child(2n + 2),
.userstop tr:nth-child(2n + 2) {
    background-color: #AAA !important;
    color: #333 !important;
}
.pm_list {
    color: black !important;
}
select,
:selection {
    background-color: silver !important;
    color: black !important;
}
.pm_progress_bar {
    background-color: #666 !important;
}
a {
    color: black !important;
}
.bb-pane {
    color: black !important;
}
.status-online::after {
    background-color: black !important;
}
}
