style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(chrome://browser/content/browser.xul) {
  .tab-image-left,
  .tab-image-middle,
  .tab-image-right,
  .tab-close-button,
  .tabs-bottom 
  .tabbrowser-strip,
  .tabs-newbutton,
  .tabs-closebutton,
  .tabbrowser-tabs {
    background-color: transparent !important;
    background-image: none !important;
    -moz-appearance: none;
    -moz-border-radius: 0 !important;
    border: 0 solid black !important;
  }

  .tabbrowser-strip {
    -moz-appearance: toolbox !important;
    border-bottom: solid 1px #ccc !important;
    -moz-border-top-colors: transparent !important;
    -moz-border-left-colors: transparent !important;
    -moz-border-right-colors: transparent !important;
  }

  .tabs-newbutton:hover {
    background-color: yellow !important;
    background-image: none !important;
  }
  .tabs-closebutton:hover {
    background-color: yellow !important;
    background-image: none !important;
  }

  .tabbrowser-tab
  {
    font-weight: normal !important;
    font-family: monospace !important;
    font-size: 10pt !important;
    margin: 1px 1px !important;
    border: thin outset lightgray !important;
    -moz-appearance: none !important;
    -moz-border-radius: 0 !important;
  }
  .tabbrowser-tab:not([selected="true"]) {
    color: gray !important;
  }
  .tabbrowser-tab:not([selected="true"]):hover {
    color: gray !important;
    background-color: yellow !important;
  }
  .tabbrowser-tab[selected="true"] {
    color: black !important;
    /* background-color: #efeee9 !important; */
  }

  .tabs-alltabs-box,
  .tabs-alltabs-box-animate,
  .scrollbutton-down-box,
  .scrollbutton-down-box-animate
  {
    background: none !important;
  }

  .scrollbutton-up,
  .scrollbutton-down,
  .tabs-alltabs-button {
    margin: 2px 1px !important;
    /* border: thin outset lightgray !important; */
    background-color: transparent !important;
    background-image: none !important;
    -moz-appearance: none !important;
    -moz-border-radius: 0 !important;
  }
  .scrollbutton-up:hover,
  .scrollbutton-up:hover:active,
  .scrollbutton-down:hover,
  .scrollbutton-down:hover:active {
    background-color: yellow !important;
  }

  .tabs-alltabs-button,
  .tabs-alltabs-button:hover,
  .tabs-alltabs-button:hover:active {
    display: none !important;
    background-color: yellow !important;
  }

  stack.tabs-alltabs-stack {
    display: none !important;
  }
}
