style.css:
@-moz-document domain("store.steampowered.com"), domain("steamcommunity.com") {
/*

Theme: Steam Flat v1.1
Author: DaveyG103
Version: 1.2.0

*/

/**
 *Table of Contents
 *
 * 1  - Links
 * 2  - Buttons
 * 3  - Input boxes
 * 4  - Dropdown menus
 * 5  - Statuses
 * 6  - Navbar
 * 7  - Profile Showcases
 * 8  - Recently Played
 * 9  - Alerts
 * 10 - Inventory
 * 11 - Filters
 * 12 - Video Player
 * 13 - Store
 * 14 - Carousel
 */

/*******/
/*Links*/
/*******/
a.whiteLink:hover,
.whiteLink:hover,
a.linkStandard:hover,
a.linkTitle:hover,
a.underlinedLink:hover,
span.underlinedLink:hover {
	color: #6bb4ff;
}

/*********/
/*Buttons*/
/*********/
/**
 * Outline
 */
button:focus {
	outline: none;
}

/**
 * Profile Header buttons
 */
.profile_header_actions {
	display: flex;
	justify-content: space-between;
}

.btn_profile_action {
	background: rgba(0,0,0,.2);
	border: 1px solid #000;
	color: #6bb4ff!important;
	text-align: center;
	transition: background 150ms ease-in;	
}

.profile_header_actions a:first-child {
	flex-basis: 65%;
}

.profile_header_actions a:last-child {
	flex-basis: 30%;
}

.btn_profile_action:hover,
.btn_profile_action.focus {
	background: rgba(0,0,0,.4);
}

.btn_profile_action > span,
.btn_profile_action:hover > span,
.btn_profile_action.focus > span {
	background: none;
}

/**
 * Pagination buttons
 */
.pagebtn,
a.pagebtn {
	background: rgba(103,193,245,.2);
	color: #6bb4ff!important;
	transition: background 150ms ease-in;
}

.pagebtn:hover,
a.pagebtn:hover {
	background: rgba(103,193,245,.5);
	color: #6bb4ff!important;
}

.pagebtn.disabled,
.pagebtn.disabled:hover,
a.pagebtn.disabled,
a.pagebtn.disabled:hover {
	background: rgba(103,193,245,.05);
	border: none;
}

/**
 * btn-green
 */
.btn_green_white_innerfade {
	background: #689826;
	color: #fff!important;
	transition: background 150ms ease-in;
}

.btn_green_white_innerfade:not(.btn_disabled):not(:disabled):not(.btn_active):not(.active):hover {
	background: #547921;
}

.btn_green_white_innerfade > span,
.btn_green_white_innerfade:not(.btn_disabled):not(:disabled):not(.btn_active):not(.active):hover > span {
	background: none;
}

/**
 * btn-dark-blue
 */
.btnv6_blue_hoverfade,
.btn_darkblue_white_innerfade {
	background: rgba(103,193,245,.2);
	color: #6bb4ff!important;
	transition: background 150ms ease-in;
}

.btn_darkblue_white_innerfade > span {
	background: none;
}

.btnv6_blue_hoverfade:not(.btn_disabled):not(:disabled):not(.btn_active):not(.active):hover,
.btn_darkblue_white_innerfade:not(.btn_disabled):not(:disabled):not(.btn_active):not(.active):hover {
	background: rgba(103,193,245,.5);
	color: #6bb4ff!important;
}

/**
 * btn-white
 */
.btnv6_white_transparent {
	transition: background 150ms ease-in;
}

.btnv6_white_transparent > span {
	border: 1px solid #fff;
}

.btnv6_white_transparent:hover {
	background: rgba(255,255,255,.2);
}

/**
 * btn-grey
 */
.btn_grey_white_innerfade {
	background: #747a80;
	transition: background 150ms ease-in;
}

.btn_grey_white_innerfade:not(.btn_disabled):not(:disabled):not(.btn_active):not(.active):hover {
	background: #5a636b;
}

.btn_grey_white_innerfade > span,
.btn_grey_white_innerfade:not(.btn_disabled):not(:disabled):not(.btn_active):not(.active):hover > span {
	background: none;
}

/**
 * btn-dark-grey
 */
.btn_grey_black {
	background: rgba(0,0,0,.3);
	color: #6bb4ff!important;
	transition: background 150ms ease-in;
}

.btn_grey_black:not(.btn_disabled):not(:disabled):not(.btn_active):not(.active):hover {
	background: rgba(0,0,0,.6);
	color: #6bb4ff!important;
}

/**
 * btn-black
 */
.btn_black {
	background: #191919;
	transition: background 150ms ease-in;
}

.btn_black:not(.btn_disabled):not(:disabled):not(.btn_active):not(.active):hover {
	background: #51708a;
	color: #fff!important;
}

.btn_details_arrow.down {
	background-position: 30px 0px;
}

/**
 * Join/watch buttons
 */

.profile_in_game_joingame {
	display: flex;
	justify-content: center;
}

.profile_in_game_joingame a:first-child {
	border-radius: 4px 0 0 4px;
}

.profile_in_game_joingame a:only-child {
	border-radius: 4px!important;
}

.profile_in_game_joingame a:last-child {
	border-radius: 0 4px 4px 0;
}

/**
 * Inventory sell item button
 */
.item_market_action_button_green {
	border-radius: 4px;
	overflow: hidden;
}

.item_market_action_button_green > span {
	background: #689826;
	transition: background 150ms ease-in;
}

.item_market_action_button_green:hover > span {
	background: #547921;
}

/*************/
/*Input Boxes*/
/*************/
/**
 * Profile Comment
 */
.profile_comment_area .commentthread_entry_quotebox {
	border: none;
	box-shadow: none;
	padding-bottom: 5px;
}

/**
 * Inputs found in profile editing page
 */
input.gray_bevel, textarea.gray_bevel, .gray_bevel, .commentthread_comment_edit_textarea_ctn {
	box-shadow: none;
}

.gray_bevel.fullwidth textarea {
	min-height: 180px;
}

.gray_bevel.fullwidth textarea::-webkit-scrollbar {
  width: 4px;
}
.gray_bevel.fullwidth textarea::-webkit-scrollbar-thumb {
  background: #6bb4ff;
  border-radius: 4px;
}
.gray_bevel.fullwidth textarea::-webkit-scrollbar-track {
	background: none;
}

/**
 * Inventory search box
 */
.filter_ctn.inventory_filters #filter_control {
	background: #242424;
	border-color: #000;
	box-shadow: none;
	color: #fff;
	height: 24px;
	padding: 0 .5rem;
}

.filter_ctn.inventory_filters #filter_control::placeholder {
	font-style: normal;
	color: rgba(255,255,255,.5);
}

#filter_clear_btn {
	top: 9px;
}

/**
 * Store search box
 */
.searchbox,
.searchbox:hover {
	background: #354965;
	border: none;
	box-shadow: none;
}

.searchbox input {
	color: #fff;
	margin: 0;
}

.searchbox input::placeholder {
	color: rgba(255,255,255,.5);
	font-style: normal;
	text-shadow: none;
}

a#store_search_link img, a#store_search_link img:hover {
	background: url(https://upload.wikimedia.org/wikipedia/commons/b/bd/VisualEditor_-_Icon_-_Search-big_-_white.svg) no-repeat center center / 25px 25px;
	border-radius: 2px;
	transition: opacity 200ms ease-in;
}

a#store_search_link img:hover {
	opacity: .5;
}

/****************/
/*Dropdown Menus*/
/****************/
/**
 * Dropdown lists
 */
select.gray_bevel {
	border-color: #000;
	box-shadow: none;
	color: #fff;
	padding: 0 .25rem;
	margin-bottom: 0;
}

/**
 * Site navigation
 */
.supernav_content,
body.v6 .supernav_content {
	background: none;
	box-shadow: none;
	padding: .5rem 0 0 0;
	width: 132px;
}

.supernav_content > div[class^=submenu] {
	background: #171a21;
	border: 1px solid #000;
	box-shadow: 2px 2px 20px -2px #000;
}

/**
 * - Site navigation items
 */
div#global_header .submenuitem {
	padding-left: .5rem;
}

div#global_header .submenuitem.active,
div#global_header .submenuitem:hover {
	background: #212d3d;
}

/**
 * User actions dropdown
 */
.popup_block_new .popup_body {
	border: 1px solid #000;
}

/**
 * Store search box
*/

.search_suggest {
	box-shadow: 5px 5px 20px -5px #000;
	top: 42px;
	right: 0;
}

body.v6 .store_nav .popup_block_new .popup_body,
body.v6 #footer_nav .popup_block_new .popup_body {
	background: #191c23;
	padding: 0;
}

.store_nav .popup_menu .popup_menu_item,
.search_area .popup_body .match .match_name {
	color: #fff;
}

.search_area .popup_body .match,
.search_area .popup_body:hover .match{
	color: #29e428;
}

.store_nav .popup_menu .popup_menu_item:hover {
	background: #212d3d;
}

.search_suggest .match {
	border-top: none;
	border-bottom: 1px solid rgba(255,255,255,.05);
}

body.v6 .store_nav .popup_menu .hr,
body.v6 #footer_nav .popup_menu .hr {
	background: rgba(255,255,255,.15);
}

body.v6 .store_nav .popup_menu_subheader,
body.v6 #footer_nav .popup_menu_subheader {
	color: #6bb4ff;
}

/**
 * Dropdown list in steam tab in inventory
 */
#contextselect {
	border-color: #000;
	background-color: #242424;
}

#contextselect_options_contexts {
	border: 1px solid #000;
	border-top: 0;
	left: -1px;
}

#contextselect_options .context_name {
	border-bottom: 0;
	cursor: pointer;
}

#contextselect_options div[class^=shadow_] {
	display: none;
}

/**********/
/*Statuses*/
/**********/
/**
 * Profile Status
 */

.responsive_status_info {
	text-align: center;
}

/**
 * status-ingame
*/
.in-game .profile_in_game_header,
.in-game .profile_in_game_name {
	color: #98e034;
}

/**
 * status-online
*/
.online .profile_in_game_header {
	color: #6bb4ff;
}

/**
 * status-offline
*/
.offline .profile_in_game_header,
.offline .profile_in_game_name {
	color: #b6bfc7;
}

/**
 * Avatar Status
   * avatar-ingame
 *
*/
.playerAvatar.in-game {
	background: #98e034;
}

.playerAvatar.in-game img{
	background: none;
}

/**
 * avatar-online
 */
.playerAvatar.online {
	background: #6bb4ff;
}

.playerAvatar.online img {
	background: none;
}

/**
 * avatar-offline
 */
.playerAvatar.offline {
	background: #747a80;
}

.playerAvatar.offline img {
	background: none;
}

/********/
/*Navbars*/
/********/
/**
 * Navbar wrapper
 */
div#global_header {
	border-bottom: 1px solid #000;
}

div#global_header .content {
	align-items: center;
	display: flex;
	height: 60px;
	justify-content: space-between;
	margin: 0;
	right: 0;
	width: 100%;
}

/**
 * Steam logo
*/
div#global_header div.logo {
	height: 32px;
	margin: 0;
	opacity: .5;
	padding: 0;
	transition: all 200ms ease-out;
	width: auto;
}

div#global_header div.logo:hover {
	opacity: 1;
}

div#global_header div.logo > #logo_holder img{
	height: 32px;
	width: auto;
}

/**
 * Navbar list
 */
#global_header .supernav_container,
div#global_actions {
	position: relative;
	left: auto;
}

#global_header .supernav_container {
	align-items: center;
	display: flex;
}

/**
 * Navbar items
 */
div#global_header .menuitem {
	padding: 0 .5rem;
}

/**
 * Navbar user actions
 */
div#global_actions {
	top: 0;
	height: 38px;
}

/*Store Nav*/
#store_nav_area .store_nav_bg,
.home_page_body_ctn.has_takeover #store_nav_area .store_nav_bg {
	background: #10283b;
	border: 1px solid #000;
	box-shadow: none;
	border-radius: 2px;
}

/*Store Nav Items*/
.store_nav .tab {
	border-right: 1px solid #000;
}

.store_nav .tab:hover,
.store_nav .tab.focus {
	background: #191c23;
}

.store_nav .tab > span,
.store_nav .tab:hover > span,
.store_nav .tab.focus > span {
	background: none;
	color: #fff;
}

/**************/
/*Profile Page*/
/**************/
/*Header*/
.profile_header_bg,
.profile_small_header_bg {
	background: none;
	padding: 0;
	z-index: 1;
}

.profile_header_bg_texture,
.profile_small_header_texture {
	background-image: linear-gradient(to right bottom, #2f4067, #2a3959, #26314b, #212a3e, #1d2331);
	border: 1px solid #000;
	border-top: none;
}

.has_profile_background .profile_header_bg {
	margin-bottom: 13px;
}

.profile_content {
	padding-top: 13px;
}

.profile_background_holder_content,
.profile_background_overlay_content {
	background: none;
}

.profile_background_image_content {
	background: rgba(24,24,24,.85)!important;
}

/*Badge Section*/
.profile_header_badgeinfo {
	margin-left: .5rem;
}

.profile_header_badge {
	background: rgba(0,0,0,.2);
	border: 1px solid #000;
	box-shadow: none;
	z-index: 10;
}

/*******************/
/*Profile Showcases*/
/*******************/
/**
 * Showcase container
 */
.profile_customization {
	/*background-image: linear-gradient(to right bottom, #2f4067, #283655, #222d43, #1d2331, #171a21); slightly darker*/
	background-image: linear-gradient(to right bottom, #2f4067, #2a3959, #26314b, #212a3e, #1d2331);
}

.profile_customization_header {
	color: #6bb4ff;
}

/**
 * Showcase inner
 */
.showcase_content_bg {
	background: rgba(0,0,0,.4);
}

/**
 * Stats row
 */
.showcase_stats_row {
	display: flex;
	justify-content: space-between;
}

.showcase_stats_row .showcase_stat {
	margin: 0!important;
	text-align: center;
}

.showcase_stat .label {
	color: #fff;
}

/**
 * Achievements showcase
 */
.achievement_showcase .showcase_content_bg {
	display: flex;
	justify-content: space-around;
}

.showcase_achievement {
	margin: 0;
}

.achievement_showcase .showcase_content_bg div[style^=clear] {
	display: none;
}

/**
 * Badge collector
 */
.badge_showcase .showcase_stat {
	width: 50%;
}

/**
 * Favourite game
 */
.favoritegame_showcase_game.showcase_slot {
	align-items: center;
	display: flex;
}

.favorite_game_cap img {
	border-radius: 4px;
}

.showcase_item_detail_title {
	padding: 0;
}

.favoritegame_showcase .showcase_stats_row {
	display: flex;
}

.favoritegame_showcase .showcase_stat {
	width: 100%;
}

/**
 * Favourite group
 */
.favoritegroup_stats {
	background: #192b3b;
	border-radius: 4px;
}

.favoritegroup_stats .showcase_stat {
	width: 25%;
}

.showcase_stat.favoritegroup_ingame .value {
	color: #98e034;
}

.showcase_stat.favoritegroup_inchat {
	background: none;
	padding: 4px 0;
}

/**
 * Game collector
*/
.gamecollector_showcase .showcase_stat {
	width: 25%;
}

.gamecollector_showcase img.game_capsule {
	border-radius: 3px;
}

/**
 * Item showcase
 */
.item_showcase_item {
	background:rgba(0,0,0,.4)!important;
	transition: background 200ms;
}

.item_showcase_item:hover {
	background: rgba(0,0,0,.6)!important;
}

/**
 * Item counter
 */
.item_showcase .item_count_stat {
	bottom: 2.25rem;
	position: absolute;
	right: 3.75rem;
}

/**
 * Items up for trade
 */
.showcase_stats_trading > .showcase_stat {
	width: 33.333333%;
}

/**
 * Rare achievements showcase
 */
.achievement_showcase .showcase_stat {
	width: 33.333333%;
}

.achievement_showcase .showcase_stat:nth-child(2) {
	order: 3;
}

.game_info_achievement.plus_more, .showcase_achievement.plus_more {
	background: rgba(0,0,0,.3);
	border: 1px solid #000;
	box-shadow: none;
}

/**
 * Review showcase comment icon
 */
.ico16.comment {
	margin-top: -1px;
}

.btn_grey_black:hover .ico16.comment {
	background-position: 0 0!important;
}

/**
 * Screenshots showcase
 */
.screenshot_showcase_smallscreenshot.screenshot_count {
	background: rgba(0,0,0,.4);
}

.screenshot_showcase_screenshot {
	border: 1px solid rgba(107, 180, 255,.3);
	transition: all 200ms;
}

.screenshot_showcase_screenshot:hover {
	border: 1px solid rgba(107, 180, 255,1);
}

/**
 * add a showcase
 */
.profile_customization.customization_edit.none_selected {
	background: rgba(23,30,46,.2);
	background: rgba(23,30,46,1);
	transition: all 200ms ease-out;
}

.profile_customization_area .profile_customization.customization_edit.none_selected:hover {
	background: rgba(23,30,46,1);
	border-color: #6bb4ff;
}

.profile_customization_area .profile_customization.customization_edit.none_selected:active {
	background: #222a3c;
}

/*****************/
/*Recently Played*/
/*****************/
/**
 * recently played container
 */
.recent_game .game_info_stats, .favoritegame_showcase .game_info_stats {
	background-image: linear-gradient(to left top, #1a1f2a, #22242d, #29292f, #2f2f32, #353535);
	border: 1px solid #000;
	box-shadow: none;
}

/**
 * game badge
 */
.game_info_badge_border {
	background: rgba(0,0,0,.3);
	border: none;
	box-shadow: none;
}

.game_info_stats_rule {
	background: none;
	border-color: rgba(100,100,100,.5);
}

/**
 * Achievement progress bar
 */
.achievement_progress_bar_ctn .progress_bar {
	background-image: linear-gradient(to left top, #296aac, #3574b5, #3f7dbd, #4a87c6, #5491cf);
}

/********/
/*Alerts*/
/********/
/**
 * User notification
 */
.global_header_toggle_button.green,
.global_header_toggle_button.has_notifications {
	background: #6ca936;
}

/**
 * Profile saved
 */
.saved_changes_msg {
	border-color: #6bb4ff;
	color: #6bb4ff;
}

/**
 * Inventory error
 */
.inventory_load_error {
    background: rgba(99, 41, 41, .25);
}

/**
 * Inventory new items
 */
.alertbox_header {
	background: #76a709;
	border-radius: 4px 4px 0 0;
	overflow: hidden;
}

.alertbox_header .leftcap,
.alertbox_header .rightcap {
	background: #76a709;
}

.alertbox_content {
	border-color: #76a709;
}

.welcome_dismiss {
	border-radius: 4px;
	transition: opacity 200ms;
}

.welcome_dismiss:hover {
	opacity: .7;
}

/***********/
/*Inventory*/
/***********/
/**
 * inventory tabs
*/
.games_list_tabs {
	border-color: #000;
	padding: 0;
	width: 924px;
}

.games_list_tab_separator,
.games_list_tab_row_separator {
	display: none;
}

a.games_list_tab {
	align-items: center;
	border: none;
	display: flex;
	flex-direction: column;
	height: 80px;
	padding: 0;
	width: 57.75px;
}

a.games_list_tab:hover:not(.active) {
	background: #2e2e2e;
}

.games_list_tab_icon {
	height: 32px;
	padding: 0;
}

.games_list_tab_name {
	display: none;
}

.games_list_tab_number {
	padding: 0;
}

.games_list_tab.active {
	border: none;
	box-shadow: none;
}

/**
 * inventory content
 */
#tabcontent_inventory {
	border-color: #000;
	border-top: 0;
}

.view_inventory_logo {
	display: flex;
	justify-content: center;
}

.trade_item_box {
	background: #242424;
}

.view_inventory_page .item {
	/*
	Can't simply use 'all' because for some reason the
	background shifts when hovered over
	*/
	transition: border 200ms ease-out, opacity 200ms ease-out;
}

.view_inventory_page .item:not(.activeInfo) {
	opacity: .5;
}

.view_inventory_page .item:hover {
	opacity: 1;
}

.view_inventory_page .item.activeInfo {
	border-color: rgb(255, 215, 0) !important;
	background: rgba(255, 215, 0, .25) !important;
}

.inventory_page_right {
	/*min-height: auto!important;*/
}

.item_desc_icon_center {
	align-items: center;
	display: flex;
	justify-content: center;
}

.hover_item_name {
	color: #fff;
	font-size: 1rem;
}

/*Description box coloured text*/
/*Gem value*/
.item_scrap_value {
	color: #6bb4ff;
}

/*Coupon discount*/
.descriptor[style="color: rgb(122, 159, 197);"] {
	color: #6bb4ff!important;
}

/*Valid until*/
.descriptor[style="color: rgb(167, 81, 36);"] {
	color: #fa7b38!important;
}

/**/


/**
 * Filters
 */
.econ_tag_filter_label {
	cursor: pointer;
	transition: opacity 200ms;
}

.econ_tag_filter_label:hover {
	opacity: .7;
}

#filter_control.filtered,
#inventories.filtered,
.filter_tag_options.filtered {
	border-color: #6bb4ff;
}

#empty_filtered_inventory_page {
	border-color: #6bb4ff!important;
}

/**************/
/*Video Player*/
/**************/
.BroadcastPlayer {
	cursor: pointer;
}

.dash_video_live_banner,
.dash_live_edge .dash_video_live_banner {
	background: rgba(0,0,0,.5);
}

.dash_video_live_banner:before {
	background: red;
	border-radius: 50%;
	content: '';
	height: 15px;
	left: 5px;
	top: 3px;
	position: absolute;
	width: 15px;
}

.dash_video_live_banner:after {
	color: #fff;
	content: 'LIVE';
	left: 24px;
	font-size: 1rem;
	position: absolute;
	top: 3px;
}

.dash_overlay .volume_slider {
	background: none;
}

.dash_overlay .volume_slider:before {
	background: #555;
	content: '';
	height: 5px;
	top: 55%;
	transform: translateY(-50%);
	position: absolute;
	width: 100%;
}

.dash_overlay .control_container {
	background: rgba(0,0,0,.5);
}

.dash_overlay .progress_bar {
	background-color: #6bb4ff;
}

/*Quality*/
.dash_overlay .video_quality_label,
.dash_overlay .settings_icon:hover div,
.dash_overlay .video_representation_live_video span {
	color: #fff;
}

.dash_overlay .video_representation_live_video span.selected {
	color: #6bb4ff;
}


/*Timestamp*/
.dash_overlay .right_time {
	color: #fff;
}

.dash_overlay .progress_time_info {
	border: 1px solid #fff;
	color: #fff;
}

/*******/
/*Store*/
/*******/
/*
 * Removing sections that can be a nuisance to scroll past.
 * If you want them, feel free to remove any of these without problem.
 */
.friends_recently_purchased,
.discovery_queue_ctn,
.steam_curators_ctn,
.recently_updated_block,
.hardware_ctn {
	display: none!important;
}

/*Under 7 quidde / 10 buccs*/
.home_ctn.tab_container + .home_ctn {
	overflow: visible;
	height: 625px;
	position: absolute;
	right: 6rem;
	top: 3px;
}

.home_ctn.tab_container + .home_ctn .home_page_content {
	height: 100%;
	padding: 0;
	width: 229px;
}

.specials_under10 {
	margin: 0;
	height: 100%;
}

.specials_under10 h2 {
	font-size: 1.5rem!important;
	padding: 0!important;
	text-align: center;
	width: auto!important;
}

.specials_under10 h2 > .right {
	display: block!important;
	float: none!important;
}

.store_capsule_container {
	width: 100%;
}

.store_capsule_container > div {
	display: flex;
	flex-direction: column;
	width: 100%!important;
}

.specials_under10 .store_capsule {
	border: 1px solid #000;
	box-shadow: none;
	margin-bottom: 1rem;
}

.specials_under10 .carousel_thumbs {
	bottom: -2rem;
	position: absolute;
	left: 50%;
	transform: translateX(-50%);
}

.specials_under10 .arrow,
.wishlist_on_sale .arrow {
	top: 50%;
	transform: translateY(-50%);
}

/*Auto-loading content*/
#content_callout,
#footer_spacer,
#LoadingContent,
.home_ctn.home_content_ctn {
	display: none!important;
}

body.v6 #footer {
	position: relative;
}
/**********/
/*Carousel*/
/**********/
.arrow.left {
	border-radius: 10px 0 0 10px;
}

.arrow.right {
	border-radius: 0 10px 10px 0;
}

.carousel_container.maincap .carousel_items {
	border: 1px solid #000;
	box-shadow: none;
}

.store_main_capsule .capsule,
.store_main_capsule .screenshot {
	box-shadow: inset 0px 0px 100px 20px #000;
}

.discount_block .discount_final_price {
	color: #6bb4ff;
}

.store_main_capsule .platforms .platform_img {
	opacity: .5;
	transition: all 200ms ease-in;
}

.store_main_capsule .platforms .platform_img:hover {
	opacity: 1;
}

/*Game info hover box*/
.game_hover_box {
	background: #191c23;
	box-shadow: none;
	border: 1px solid #6bb4ff;
	color: #fff;
	margin-top: 7px;
}

.game_hover_box h4 {
	color: #6bb4ff;
	font-size: 1.25rem;
}

.hover_tag_row .app_tag {
	color: #112846;
}

.game_review_summary.positive {
	color: #6bb4ff;
}

.game_hover .hover_arrow_left,
.game_hover .hover_arrow_right {
	background: none;
	border: 9px solid transparent;
	height: auto;
	width: auto;
	z-index: 1;
}

.game_hover .hover_arrow_left {
	border-right: 9px solid #6bb4ff;
	left: -5px;
}

.game_hover .hover_arrow_right {
	border-left: 9px solid #6bb4ff;
	right: -5px;
}

.game_hover .hover_arrow_left:after,
.game_hover .hover_arrow_right:after {
	border: 9px solid transparent;
	content: '';
	position: absolute;
	top: -9px;
	z-index: 1000;
}

.game_hover .hover_arrow_left:after {
	border-right: 9px solid #191c23;
	left: -7px;
}

.game_hover .hover_arrow_right:after {
	border-left: 9px solid #191c23;
	right: -7px;
}

/*New and trending*/
.tab_item.focus {
	background: #191c23;
	color: #fff;
}

.tab_item_details,
.tab_item.focus .tab_item_name {
	color: #fff;
}

.tab_item.focus .discount_block .discount_final_price {
	color: #6bb4ff;
}

.tab_item_details span.platform_img {
	opacity: .7;
}

.tab_item.focus .tab_item_details span.platform_img {
	opacity: 1;
}

.tab_item.focus .tab_item_details span.platform_img.win {
	background: url('https://steamstore-a.akamaihd.net/public/images/v6/icon_platform_win.png?v=3');
}

.tab_item.focus .tab_item_details span.platform_img.mac {
	background: url('https://steamstore-a.akamaihd.net/public/images/v6/icon_platform_mac.png');
}

.tab_item.focus .tab_item_details span.platform_img.linux {
	background: url('https://steamstore-a.akamaihd.net/public/images/v6/icon_platform_linux.png');
}

#tab_preview_container {
	background: #191c23;
	color: #fff;
}

#tab_preview_container h2 {
	color: #fff;
}

