/*Page Footer*/
/*************/

#footer_responsive_optin_spacer {
	height: calc(139px + 2rem)!important;
}

/*******/
/*Games*/
/*******/
#games_list_rows {
	display: flex;
	flex-flow: row wrap;
	justify-content: space-between;
}

.gameListRow {
	background: rgba(0,0,0,.3);
	border-radius: 4px;
	flex-basis: 31%;
	margin-bottom: .5rem;
	padding-bottom: 2.75rem;
}

.gameListRowLogo {
	display: flex;
	float: none;
	max-width: 100%;
	width: 100%;
}

.gameListRowLogo img {
	border-radius: 4px;
	height: 60px;
	width: auto;
}

.gameListRowItem {
	display: flex;
	flex-flow: row wrap;
	width: 100%;
}

/**
 * If you're on your own games page
 * and you have games already installed
 * or games ready to be installed
*/

/*Current device*/
.clientConnArea {
	background-image: linear-gradient(to bottom, #263646, #2a3a4a, #2e3f4f, #334353, #374858);
}

.clientConnItemBlock {
	right: 0;
	top: -4rem;
	position: absolute;
}

.clientConnItemTextBlock {
	
}

.clientConnItemIcon {
	border-radius: 4px;
}

.gameListRowItem .hours_played {
	flex-basis: 100%;
}

.bottom_controls {
	display: flex;
	flex-flow: row wrap;
	justify-content: space-between;
	flex-basis: 100%;
}

.bottom_controls .pullup_item {
	display: flex;
	justify-content: center;
	margin-right: 0;
	margin-bottom: .35rem;
	position: relative;
	flex-basis: 46%;
}

.pullup_item .menuarrow {
	height: 4px;
	position: absolute;
	right: .5rem;
	top: 50%;
	transform: translateY(-50%);
	width: 9px;
}

.bottom_controls .pullup_item:nth-child(2) + div > img {
	display: none;
}
}
