style.css:
@-moz-document domain("adnmb2.com"),domain("adnmb1.com"), domain("tnmb.org") {
html {
    color: #8e8e8e !important;
    /*背景字体颜色*/
    background: rgb(24, 24, 24)!important;
    /*背景颜色*/
}
body {
    background: transparent url(/*[[image]]*/) center top fixed !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
}
#h-content {
    padding: 20px 5% 5% 5%;
    column-count: 3;
    column-width: 400px;
    column-gap: 24px;
    margin:0 auto;
    max-width:1740px;
}
.uk-container {
    padding: 0px;
    margin:auto;
}
.h-threads-list{
    margin:auto;
    max-width: 530px;
}
.h-threads-item {
    height: 100%;
    overflow-x: hidden;
    overflow-y: auto;
    background-color: rgba(50, 50, 50, .96);
    border-radius: 5px;
    padding: 8px 12px 10px 0px;
    border: 1px solid rgba(91, 91, 91, .1);
    margin:22px auto!important;
    word-break:break-all;
}
#h-menu {
    transition:left .3s ease, box-shadow .3s ease;
    border-right-color: #8E8E8E !important;
    position: fixed;
    overflow: hidden !important;
    text-overflow: clip;
    width: 170px;
    height: 100%!important;
    left:-160px;
    background: #303030;
    /*侧边背景颜色*/
    border-style: none;
    z-index: 9999997;
}
#h-menu:hover {
    transition:left .3s ease, box-shadow .3s;
    left:0px;
    box-shadow: 30px 0px 200px rgba(0, 0, 0, 1);
    overflow: scroll;
}
#h-menu:active {
    transition:box-shadow .01s;
    box-shadow: 30px 0px 200px rgba(0, 0, 0, .2);
}
#h-menu::after {
    background: rgba(20, 20, 20, .8);
    border: 1px solid rgba(0, 0, 0, .0);
    position: fixed;
    left: -2px;
    top: -2px;
    display: inline-block;
    font-size: 26px;
    color: rgba(0, 0, 0, .0);
    content:' ';
    width: 12px;
    height: 100%;
    vertical-align: middle;
    font-weight: bold;
    text-align: center;
    cursor: pointer;
    opacity:0;
}
#h-menu:hover:after {
    transition:opacity .3s;
    width: 100%;
    z-index: -99999;
    opacity:.8;
}
#h-menu:active:after {
    transition:width .01s .05s ,opacity .05s;
    width: 12px;
    opacity:0;
}
#h-menu-top-img img {
    transition: opacity .3s ease, box-shadow .3s ease;
    border-radius: 24px;
    opacity: .5;
}
#h-menu-top-img img:hover {
    transition: opacity .3s ease, box-shadow .3s ease;
    opacity: 1;
    box-shadow: 0px 3px 20px rgba(0, 0, 0, .2);
}
#h-menu-top-sitename:before {
    font-size: 16px;
    content:"白丝岛-JK匿名版\A";
    color: #8E8E8E;
    /* 岛字体颜色 */
    ;
}
#h-menu-top-sitename {
    width: 120px;
    max-height: 20px;
    /* 请修改为第一行的高度 */
    overflow: hidden;
}
#h-menu-search-keyword {
    transition: background .3s ease, box-shadow .3s ease;
    height: 24px;
    width: 120px !important;
    color: #888888!important;
    background-color: #464646;
    border-radius: 12px;
    border-style: none;
    padding: 0px 8px;
}
#h-menu-search-keyword:hover {
    transition: background .3s ease, box-shadow .3s ease;
    background-color: #505050;
    box-shadow: 0px 1px 5px rgba(20, 20, 20, .2), 0px 2px 10px rgba(0, 0, 0, .2);
}
#h-menu-content {
    color: #8E8E8E;
    /* 侧边字体颜色 */
}
.h-nav-parent-header, .h-nav-header, h2, span.h-threads-info-title, font[color$="#789922"] {
    /*引用颜色*/
    color: #bbb !important;
    /*标题颜色*/
    ;
}
.h-nav-item, .uk-nav-sub>li>a {
    text-indent: 10px;
    color: #8E8E8E;
    /* 侧边字体颜色 */
}
.h-active, .uk-active>span {
    background: #464646 !important;
    /*侧边高亮颜色*/
}
  .h-disabled,
.uk-disabled>span
{
    background: #353535 !important;
    color: rgba(0,0,0,0) !important;
}

.uk-pagination>li>a
{
    color: rgba(0,0,0,0);
    background: #606060;
    box-shadow: 0px 5px 5px  rgba(20,20,20,.2),0px 0px 10px  rgba(0,0,0,.1);
}
.uk-container-center, .system-message
{
    background-color: #252525 !important;
    /*公告底色*/;
}

.uk-container-center>div
{
    color: #8E8E8E !important;
    /*公告字体色*/;
}

/***********************右侧工具栏****************************/
.h-tool-btn {
    background-color: #6c6c6c !important;
    color: #fff !important;
    box-shadow: 0px 0px 15px rgba(20, 20, 20, .1), 0px 0px 20px rgba(20, 20, 20, .1);
    opacity: .9 !important;
}
.h-tool-btn:hover {
    opacity: 1 !important;
}
#h-post-form, #h-bottom-nav, .uk-pagination {
    transition: width .3s ease-out 0.5s, height .5s ease 0s, border-radius .3s 0.5s, box-shadow .3s 0.5s, opacity .5s .5s;
    background-color: #2e2e2e !important;
    border-radius: 0px;
    padding: 5px 20px;
    position: fixed;
    right: 10px;
    left: auto;
    z-index: 99;
    overflow: hidden;
    opacity:.9!important;
}
#h-bottom-nav {
    width: 0px;
    height: 30px;
    max-width: 460px;
    max-height: 60%;
    color: #e0e0e0;
    border-style: none;
}
#h-post-form {
    width: 40px;
    height: 40px;
    max-width: 500px;
    position: fixed;
    max-height: 60%;
}
.uk-pagination{
    width: 0px;
    height: 30px;
    max-width: 550px;
    position: fixed;
    max-height: 60%;
}
#h-post-form:hover, #h-bottom-nav:hover, .uk-pagination:hover {
    transition: width .3s ease-in, height .5s ease .3s, border-radius .3s, box-shadow .3s;
    width: 80%;
    border-radius: 6px;
    box-shadow: 5px 10px 40px rgba(0, 0, 0, .4);
    opacity:1!important;
}
#h-post-form:hover {
    height: 80%;
}
#h-bottom-nav:hover {
    height: 124px;
}
.uk-pagination:hover {
    height: 30px;
}
#h-post-form::after, #h-bottom-nav::after, .uk-pagination::after {
    transition:box-shadow .3s .8s;
    background: #6c6c6c;
    box-shadow: 0px 0px 15px rgba(20, 20, 20, .1), 0px 0px 20px rgba(20, 20, 20, .1);
    position: fixed;
    right: 10px;
    display: inline-block;
    font-size: 26px;
    color: #fff;
    width: 40px;
    height: 40px;
    line-height: 40px;
    vertical-align: middle;
    border-radius: 0px;
    font-weight: bold;
    text-align: center;
    cursor: pointer;
}
#h-post-form::after {
    content:'+';
}
#h-bottom-nav::after {
    content:'<';
}
.uk-pagination::after {
    content:'…';
}
#h-post-form:hover:after, #h-bottom-nav:hover:after, .uk-pagination:hover:after {
    transition:box-shadow 0s;
    box-shadow: 0px 0px 0px rgba(0, 0, 20, 0);
    position: fixed;
    left: -2px;
    top: -2px;
    width: 100%;
    height: 100%;
    opacity:0;
    z-index: -99999;
}
#h-post-form:active:after, #h-bottom-nav:active:after, .uk-pagination:active:after {
    transition:box-shadow 0s;
    box-shadow: 0px 0px 0px rgba(0, 0, 20, 0);
    position: fixed;
    right: 10px;
    width: 40px;
    height: 40px;
    opacity:0;
}
#h-post-form, #h-post-form:active:after, #h-post-form::after {
    bottom: 91px!important;
}
#h-bottom-nav, #h-bottom-nav:active:after, #h-bottom-nav::after {
    bottom: 139px !important;
}
.uk-pagination {
    bottom: 10px !important;
}
.uk-pagination:active:after, .uk-pagination::after {
    bottom:25px !important;
}

span.h-threads-info-email {
    color: #6e6e6e !important;
    /*名字颜色*/
}
div.h-threads-item-reply-main {
    transition: background .3s ease, box-shadow .3s ease;
    /* background: #464646 !important;;*/
    background: rgba(70,70,70,1) !important;
    /*卡片背景颜色*/
    box-shadow: 0px 2px 5px  rgba(20,20,20,.2),0px 0px 10px  rgba(0,0,0,.1);
    border-radius: 2px;
    padding: 8px 12px 10px;
    border-style: none;
    width:100%;
    max-width:500px;
    word-break:break-all;
}
div.h-threads-item-reply-main:hover {
    transition: background .3s ease, box-shadow .3s ease;
    background: rgba(80,80,80,1) !important;
    /*鼠标悬停卡片背景颜色*/
    box-shadow: 0px 10px 50px  rgba(20,20,20,.3),0px 5px 30px  rgba(0,0,0,.2);
}
div.h-threads-item-reply-main:active {
    transition: .01s;
    background: rgb(40, 40, 40) !important;
    box-shadow: 0px 2px 5px rgba(20, 20, 20, .15), 0px 5px 15px rgba(0, 0, 0, .1);
}
div.h-threads-item-ref {
    border-style: none!important;
    margin: -15px 0px -10px 0px!important;
    max-width:800px;
}
div.h-threads-item-reply {
    margin: 15px 0px 0px 0px;
    border-radius: 10px;
}
.h-post-form-title {
    background: #363636 !important;
    /*公告侧栏颜色*/
    color: #8E8E8E !important;
    /*公告侧栏字体颜色*/
    margin: 4px 0px 0px 0px;
}
.h-post-form-option input {
    transition: background .3s ease, box-shadow .3s ease;
    color: #8e8e8e!important;
    background-color: #464646!important;
    border-radius: 3px;
    border: 1px solid #5c5c5c;
    padding: 4px 8px 3px;
    margin: 2px 0px 0px 0px;
}
.h-post-form-option input:hover {
    transition: background-color .3s ease, box-shadow .3s ease;
    background-color: #282828!important;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, .1), 0px 0px 10px rgba(0, 0, 0, .05);
}
.h-post-form-input input {
    transition: background .3s ease, box-shadow .3s ease;
    color: #888888!important;
    background-color: #464646!important;
    border-radius: 1px;
    border: 1px solid #5c5c5c;
    padding: 5px 0px 4px;
    margin: 2px 0px 0px 0px;
}
.h-post-form-input input:hover {
    transition: background-color .3s ease, box-shadow .3s ease;
    background-color: #282828!important;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, .1), 0px 0px 10px rgba(0, 0, 0, .05);
}
#h-emot-select {
    transition: background .3s ease, box-shadow .3s ease;
    color: #888888!important;
    background-color: #464646!important;
    border-radius: 1px;
    padding: 4px 0px 3px;
    margin: 2px 0px 0px 0px;
    border: 1px solid #5c5c5c;
}
#h-emot-select:hover {
    transition: background-color .3s ease, box-shadow .3s ease;
    background-color: #282828!important;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, .1), 0px 0px 10px rgba(0, 0, 0, .05);
}
.h-post-form-textarea {
    transition: background .3s ease, box-shadow .3s ease;
    color: #888888!important;
    background-color: #464646!important;
    border-radius: 1px;
    margin: 2px 0px 0px 0px;
    border: 1px solid #5c5c5c;
    padding: 16px 16px;
}
.h-post-form-textarea:hover {
    transition: background-color .3s ease, box-shadow .3s ease;
    background-color: #282828!important;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, .1), 0px 0px 10px rgba(0, 0, 0, .05);
}
.h-threads-item-reply-icon {
    visibility: hidden;
}
.uk-breadcrumb {
    background-color: #313131;
    border-radius: 6px;
    box-shadow: 0px 0px 5px rgba(20, 20, 20, .2);
    max-width: 400px;
    margin: 0px 0px 15px 0px;
    padding: 4px 8px;
    column-span: all;
}
.h-title {
    font-size: 25px;
    margin: 0px 0px 20px;
    letter-spacing:3px;
    color:#7c7c7c!important;
    text-shadow:0 0 3px #000;
}
.h-threads-item-main {
    margin: -1px 0px 5px 0px;
    padding: 1px 0px 0px 0px;
}
hr {
    display:none;
    border-top: 1px solid rgba(142, 142, 142, .2);
}
.h-threads-info {
    background-color: rgba(37,37,37,.2);
    font-size: 10px !important;
    color: #6e6e6e !important;
    border-bottom: 1px solid #3c3c3c;
    border-radius: 3px;
    margin: -8px -12px 0px -12px;
    padding: 8px 20px;
}
.uk-text-primary {
    background-color: #ccc;
    /*po主高亮底色*/
    color: #464646 !important;
    border-radius: 8px;
    margin: 2px;
    padding: 1px 5px;
}
.h-threads-info-uid>font>sub {
    display: none;
}
a:hover {
    transition: color .1s ease, text-shadow .1s ease;
    text-decoration: none;
    color: #FFF!important;
    text-shadow: 0px 0px 2px #000, 0px 0px 5px rgba(0, 0, 0, .3)!important;
}
a:link, a:visited {
    transition: color .1s ease, text-shadow .1s ease;
    color: rgba(23, 164, 239, 1);
    text-decoration: none;
}
::selection {
    background-color: #888888!important;
    color: #fff;
}
.h-threads-img-tool-btn {
    background-color: #2c2c2c !important;
    font-size: 12px !important;
    border-radius: 3px;
    margin: 0px 2px;
    padding: 4px 6px;
}
.h-threads-img-tool-btn:hover {
    transition: background-color .3s ease, box-shadow .3s ease;
    background-color: #717171 !important;
    box-shadow: 0px 5px 50px rgba(20, 20, 20, .15), 0px 0px 30px rgba(0, 0, 0, .1);
}
.h-threads-img-tool {
    padding: 6px 6px;
    max-width: 400px;
    margin:auto!important;
}
.h-threads-img-a img {
    transition: box-shadow .2s ease;
    border-radius: 3px;
    box-shadow: 0px 2px 5px rgba(20, 20, 20, .1), 0px 0px 10px rgba(0, 0, 0, .05);
    min-width:40px;
    min-height:40px;
    background: rgba(255, 255, 255, 1) url(http://nmbimg.fastmirror.org/image/2015-11-10/5640c3ffe84d0.gif) no-repeat 50% 50%!important;
}
.h-threads-img-box.h-active .h-threads-img-a img {
    position: absolute!important;  
    left: 0!important;  
    right: 0!important;  
    margin-left: auto!important;    
    margin-right: auto!important;   
    margin-top: 10px!important;    
    margin-bottom: 20px!important;
}
.h-threads-img-box.h-active .h-threads-img-a {
    position: absolute!important;  
    left: 0!important;  
    right: 0!important;  
    margin: auto!important; 
    max-width: 90%!important;
}
.h-threads-img-a img:hover {
    transition: box-shadow .2s ease;
    box-shadow: 0px 5px 10px rgba(20, 20, 20, .15), 0px 5px 30px rgba(0, 0, 0, .1);
}
.h-threads-img-box.h-active {
    position: fixed;
    top: -4px;
    left: 0px;
    bottom:-20px;
    right:0px;
    box-shadow: 0px 10px 50px rgba(20, 20, 20, .15), 0px 5px 30px rgba(0, 0, 0, .1);
    padding: 10px 4px 10px 8px!important;
    border-radius: 5px;
    overflow: scroll;
    z-index: 9999998;
    background: rgba(62, 62, 62, .88) !important;
}
.uk-pagination {
    position: fixed;
    right: 10px;
    bottom: 10px;
}
.h-threads-item-replys .h-threads-info-id,.h-threads-item-replys .h-threads-info-report-btn {
    float: right;
}
.h-threads-info-reply-btn {
    font-size: 15px!important;
    background-color: rgba(255, 255, 255, 0);
    width: 55px;
    height: 24px;
    border-radius: 12px;
    float: right;
    vertical-align: middle;
    text-align: center;
    line-height: 24px;
    cursor: pointer;
    margin: 3% -15px 0px 0px;
    border: 1px solid rgba(110, 110, 110, .5);
}
#h-footer {
    background-color: rgba(37, 37, 37, .75)!important;
    max-width: 600px;
    border-radius: 300px;
}
}

@-moz-document regexp("^(http|https)://(adnmb1.com|adnmb2.com|tnmb.org)/((t/|Home)+.*$|Forum)") {
#h-content {
    column-count: 1;
}
.uk-container {
    margin:auto!important;
}
.h-threads-info-reply-btn {
    line-height:inherit;
    margin: 0px 0px 0px 0px;
    border:none;
}
.h-threads-info-id, .h-threads-info-report-btn {
    float: right;
}
}
