/*navigation*/
.menuLabel-title {
	color: #8590AD;
}

.menuLabel-item:hover [data-name="label"] {
	fill: #FFCE2F!important;
}

.menuLabel-item:hover .menuLabel-title {
	color: #fff;
}

.dropDown-logout-email {
	font-size: 1.2rem;
	color: #8590AD;
	opacity: 1;
}

.dropDown-logout-initials {
	border-color: #8590AD;
	color: #8590AD;
}

.topnav-link {
	color: var(--color-nav-link, #8590AD);
}

.topnav-link[aria-current=true] {
	font-weight: 400
}

.topnav-link:active,
.topnav-link:focus,
.topnav-link:hover,
.topnav-link[aria-current=true] {
	color: var(--color-nav-active, #fff);
}

.topnav-link:active > .topnav-icon,
.topnav-link:focus > .topnav-icon,
.topnav-link:hover > .topnav-icon,
.topnav-link[aria-current=true] > .topnav-icon {
	fill: var(--color-nav-active, #fff)
}


.topnav-link > svg {
	fill: var(--fillcolor-icons, #8590AD)
}

.navigation__link,
.navigation__link--groupHeader {
	padding: 0.8rem 1em 0.8rem 2rem;
	line-height: 1.3;
	color: var(--color-nav-link, #8590AD);
}

.navigation__icon {
	color: var(--fillcolor-icons, #8590AD)
}

.navigation__link:focus,
.navigation__link:hover {
	background: hsla(0, 0%, 100%, .05);
	color: var(--color-nav-link, #f6f7fa);
}

.navigation__link:focus .navigation__icon:not(.navigation__icon--expand),
.navigation__link:hover .navigation__icon:not(.navigation__icon--expand) {
	color: var(--fillcolor-icons, #FFCE2F)
}

[aria-current=page].navigation__link {
	background: var(--main-bg-color, #475482);
}

[aria-current=page].navigation__link .navigation__icon {
	color: var(--color-nav-active, #FFCE2F)
}

[aria-current=page].navigation__link,
[aria-current=page].navigation__link:hover .navigation__icon:not(.navigation__icon--expand) {
	color: var(--color-nav-active, #fff)
}


.navigation__link--groupHeader-link {
	color: var(--color-nav-link, #fff)
}

.navigation__link--groupHeader-link .navigation__icon--expand {
	color: var(--fillcolor-icons, #8590AD)
}

.navigation__link--groupHeader-link:hover {
	color: var(--color-nav-link, #f6f7fa)
}

.navigation__link--groupHeader-link:hover .navigation__icon {
	color: var(--fillcolor-icons, #fff)
}

.navigation__link--groupHeader-link {
	color: var(--fillcolor-icons, #8590AD)
}

.navigation__link--expand:focus .navigation__icon--expand,
.navigation__link--expand:hover .navigation__icon--expand,
.navigation__link--groupHeader-link:hover .navigation__icon--expand {
	color: var(--fillcolor-icons, #fff)
}

.logo-container,
.sidebar {
	width: 20rem!important;
}

.navigation__link.ptdnd-drag-hover {
	background-color: #2F3654
}

/*mail list*/
.items-column-list-inner.h100 {
	height: inherit;
}

.items-column-list {
	width: calc((100% + var(--width-sidebar, 20rem))*0.25)
}

.items-column-list-inner,
.ReactVirtualized__Grid__innerScrollContainer {
	border-right: 1px solid var(--bordercolor-input, #EFF1F4);
}

[id=secured-search] [id=conversation-list-columns],
[id=secured-search] [id=conversation-list-rows] {
	background: var(--bgcolor-view-column-detail, #f6f7fa)
}

.item-container,
.item-container-row {
	padding: .7em .5em 1.4em 1em;
	border-bottom: 1px solid var(--bordercolor-input, #EFF1F4);
	background: var(--bgcolor-item-column-list, #fff);
	height: 6.5rem
}

.conversation-icons .starbutton,
.conversation-icon-attachment {
	width: 1.2rem;
	height: 1.6rem;
}

.item-date {
	font-size: 1.1rem
}

.item-sender--smaller {
	font-size: 1.3rem
}

.item-firstline,
.item-secondline {
	margin-bottom: 0em
}

.item-secondline:empty {
	min-height: 1.25em
}

.starbutton:focus,
.starbutton:hover,
.starbutton--is-starred {
	color: #FF8B12
}

subject-text {
	font-size: 1.3rem!important;
}

.subject-text,
.time {
	color: var(--color-main-area, #828C93);
}

.flex.folderConversations-container > svg {
	color: var(--color-main-area, #828C93);
	height: 1em;
	width: 1em;
}

.statesElements-container > svg {
	color: var(--color-main-area, #828C93);
	padding-right: .2em;
	height: 1.2em;
	width: 1.2em;
}

.conversation:not(.read) .subject-num-message,
.conversation:not(.read) .subject-text,
.conversation:not(.read) .time {
	font-weight: 400
}

.read:hover {
	background: var(--bgcolor-item-column-list, #F4F9FF)!important;
}

#shape-attach:before {
	content: ""!important;
}

.conversation-icon-attachment.fill-global-grey {
	fill: #828C93;
}

.item-container.active,
.item-is-selected {
	background: var(--bgcolor-item-column-active, #E9F4FF)
}

body:not(.isDarkMode) {
	--scrollbar-thumb-color: rgba(0, 0, 0, .1);
	--scrollbar-thumb-hover-color: rgba(0, 0, 0, 0.5)
}

::-webkit-scrollbar {
	width: 1.2rem;
}

::-webkit-scrollbar-thumb {
	border: .2rem solid transparent;
	border-radius: 1.3rem;
}

.items-column-list::-webkit-scrollbar-track {
	border-left: none;
}

.navigation::-webkit-scrollbar-track {
	background-color: transparent;
	border: none;
}

.navigation::-webkit-scrollbar-thumb {
	background-color: rgba(255, 255, 255, .2);
}

::-webkit-scrollbar-thumb:horizontal:hover,
::-webkit-scrollbar-thumb:vertical:hover {
	background-color: #888FA1
}


/*toolbar*/
.toolbar {
	background: #343A53;
	box-shadow: none;
	height: 5rem;
}

.toolbar-separator {
	width: 0;
}

.elements-selector-check.pm-select-all.ml0-75.pl1 {
	padding-left: .5em
}

.pm-select-all .pm-checkbox-fakecheck,
.pm-select-all .pm-radio-fakeradio {
	border: 1px solid #3E4563;
	background-color: var(--bgcolor-aside-link, #2A2F43)
}

.pm-button.toolbar-button:hover,
.toolbar-button.pm-button--info:hover,
.toolbar-button.pm-button--redborder:hover,
.toolbar-button:hover {
	box-shadow: unset;
	background-color: var(--bgcolor-spacebar, #3E4563);
}

.pm-button.toolbar-button.is-active,
.pm-button.toolbar-button:active,
.pm-button.toolbar-button:not(div):active,
.pm-button.toolbar-button [aria-expanded=true],
.toolbar-button.is-active,
.toolbar-button.is-active.pm-button--info,
.toolbar-button.is-active.pm-button--redborder,
.toolbar-button.pm-button--info:active,
.toolbar-button.pm-button--info:not(div):active,
.toolbar-button.pm-button--info [aria-expanded=true],
.toolbar-button.pm-button--redborder:active,
.toolbar-button.pm-button--redborder:not(div):active,
.toolbar-button.pm-button--redborder [aria-expanded=true],
.toolbar-button:active,
.toolbar-button:not(div):active,
.toolbar-button [aria-expanded=true] {
	background: unset;
	background-color: var(--bgcolor-spacebar, #2A2F43)
}
/*message*/
.message-header, .trashed-messages {
    background-color: var(--bgcolor-item-column-list,#F7F9FC);
}
}
