style.css:
@-moz-document domain("www.gamestar.de") {
body {
   background: #17394E
}
#creativeAdWrapper.ad-wrapper div, .ad-wrapper object, .ad-wrapper embed, .ad-wrapper img, .ad-wrapper iframe, .ad-wrapper ins,.e3-container, .notifications .notification-container .box-spinner,.leaderboard  {
  z-index: 3 !important;  
  opacity: .1;
  transition: opacity .25s ease-in-out;
  -moz-transition: opacity .25s ease-in-out;
  -webkit-transition: opacity .25s ease-in-out;  
}
}
