style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(producthunt.com)
{
 *
  {
    border-color: rgb(61, 61, 61) !important;
  }


/*inner borders*/
  div, svg, a, section, .xLarge_18016, .contributorAccess_370ef, #comment-body,
label > span > svg, button > span > svg
  {
    background-color: rgb(34, 34, 34) !important;
    color: rgb(204, 204, 204) !important;
  }

  a, h1, h2, h3, h4, h5
    {
      color: rgb(170, 170, 170) !important;
    }

    a:visited
    {
      color: rgb(119, 119, 119) !important;
    }

    a:hover
    {
      color: rgb(204, 204, 204) !important;
    }

  /*sidebar*/
    .activeItem_c89bf .activeItem_c89bf > span:nth-child(2), .activeItem_c89bf, .activeItem_c89bf > span:nth-child(2)
    {
      border-color: rgb(34, 34, 34)  !important;
      background-color: rgb(54, 54, 54)  !important;
      color: rgb(204, 204, 204) !important;
    }

  /*sidebar text*/
    .itemText_063f9
    {
      color: rgb(204, 204, 204);
    }


    /*clicked link box shadow*/
      .contentBackground_bc644
      {
        box-shadow: 1px 0 6px 0 rgba(255, 255, 255, 0.3);
      }

      button
      {
        color: rgb(204, 204, 204) !important;
        background-color: rgb(54, 54, 54)  !important;
        background: rgb(54, 54, 54)  !important;
      }

      .icon_f5f81
      {
        background-color: rgb(54, 54, 54) !important;
      }

      .button_53e93
      {
        color: rgb(34, 34, 34) !important;
        background-color: rgb(34, 34, 34)  !important;
        background: rgb(34, 34, 34)  !important;
      }

      textarea
      {
    color: rgb(204, 204, 204) !important;
        background: rgb(34, 34, 34)  !important;
      }

     /*top search*/
      .input_277cf
      {
        color: rgb(204, 204, 204) !important;
        background-color: rgb(54, 54, 54)  !important;
      }

    /*fixed highlighed main*/
      .link_523b9:hover
      {
        border: 1px solid rgb(204, 204, 204)  !important;
        border-radius: 3px !important;
      }
}
