style.css:
@-moz-document domain("planfix.ru"), domain("planfix.com"), domain("planfix.ua") {
/*
ПланФикс - Толстые линии зависимостей (by Kirr)
2018-11-14
*/


.gant-dep-item .gant-dep-item-hline .gant-dep-item-hline-fill {
    height: 2px;
    /*background: rgba(255, 29, 50, 0.5);*/
    margin-top: 0px;
    margin-left: -1px;
    padding-bottom: 0px;
    padding-right: 1px;
    border-radius: 3px/1px;
}

.gant-dep-item .gant-dep-item-vline .gant-dep-item-vline-fill {
    width: 2px;
    /*background: rgba(32, 255, 29, 0.5);*/
    margin-top: 0px;
    margin-left: -1px;
    padding-bottom: 1px;
    padding-right: 0px;
    border-radius: 1px/3px;
}


.gant-dep-item.gant-dep-item-shadow .gant-dep-item-triangle-left {
    background-size: contain !important;
    background-repeat: round !important;    
}

.gant-dep-item.gant-dep-item-shadow .gant-dep-item-triangle-left,
.gant-dep-item .gant-dep-item-triangle-left,
.gant-dep-item:hover .gant-dep-item-triangle-left {
    height: 6px;
    width: 5px;
    background-size: contain;
    background-position: left;
    background-repeat: round;
    margin-left: -1px;
}

.gant-dep-item.gant-dep-item-shadow .gant-dep-item-triangle-right {
    background-size: contain !important;
    background-repeat: round !important;    
}

.gant-dep-item.gant-dep-item-shadow .gant-dep-item-triangle-right,
.gant-dep-item .gant-dep-item-triangle-right,
.gant-dep-item:hover .gant-dep-item-triangle-right {
    height: 6px;
    width: 5px;
    background-size: contain;
    background-position: left;
    background-repeat: round;
    margin-left: -1px;
}

.gant-dep-item.gant-dep-item-shadow .gant-dep-item-triangle-bottom {
    background-size: contain !important;
    background-repeat: round !important;    
}

.gant-dep-item.gant-dep-item-shadow .gant-dep-item-triangle-bottom,
.gant-dep-item .gant-dep-item-triangle-bottom,
.gant-dep-item:hover .gant-dep-item-triangle-bottom {
    height: 5px;
    width: 6px;
    background-size: contain;
    background-position: top;
    background-repeat: round;
    margin-left: -1px;
    margin-top: -2px;
}

.gant-dep-item.gant-dep-item-shadow .gant-dep-item-triangle-up {
    background-size: contain !important;
    background-repeat: round !important;    
}

.gant-dep-item.gant-dep-item-shadow .gant-dep-item-triangle-up,
.gant-dep-item .gant-dep-item-triangle-up,
.gant-dep-item:hover .gant-dep-item-triangle-up {
    height: 5px;
    width: 6px;
    background-size: contain;
    background-position: top;
    background-repeat: round;
    margin-left: -1px;
    margin-top: 0px;
}

}
