style.css:
@-moz-document domain("forum.planfix.ru") {
/*
ПланФикс - Цвета для форума, №1 (by Kirr)
2018-11-14
*/


html, body {
    background-color: #b6c7b5;
    color: #353535;
}

li.row {
    border-bottom-color: rgba(0, 0, 0, 0.1);
}

a.arrow-left, a.arrow-right, .button.button-secondary, .button2, a.arrow-left:hover, a.arrow-right:hover, .button.button-secondary:hover, .button2:hover, a.arrow-left:focus, a.arrow-right:focus, .button.button-secondary:focus, .button2:focus {
    background-color: #dfe7df;
}

.postbody .button.button-secondary:not(:hover) {
    background-color: transparent;
}

.search-box {
    background-color: rgba(255, 255, 255, 0.88);
}

.button1, .button-large, .button1:hover, .button-large:hover, .button1:focus, .button-large:focus {
    background-color: #ce4e79;
}

.search-box .inputbox:focus + .button, .search-box .inputbox:focus + .button + .button, .search-box:hover .button {
    background-color: #ce4e79!important;
    color: #FFFFFF!important;
}

a.arrow-left:hover, a.arrow-right:hover, .button.button-secondary:hover, .button2:hover {
    opacity: 0.8;
    box-shadow: 1px 1px 3px 0px rgba(0,0,0,0.3);
}

.button:hover, .button1:hover, .button-large:hover {
    opacity: 0.8;
    box-shadow: 1px 1px 3px 0px rgba(0,0,0,0.3);
}


ul.topiclist li.header {
    border: none;
    border-bottom: solid 4px rgba(182, 199, 181, 0.8);
}

.global_unread:before, .global_unread_mine:before, .global_unread_locked:before, .global_unread_locked_mine:before, .announce_unread:before, .announce_unread_mine:before, .announce_unread_locked:before, .announce_unread_locked_mine:before, .forum_unread:before, .forum_unread_locked:before, .forum_unread_subforum:before, .sticky_unread:before, .sticky_unread_mine:before, .sticky_unread_locked:before, .sticky_unread_locked_mine:before, .topic_unread:before, .topic_unread_mine:before, .topic_unread_hot:before, .topic_unread_hot_mine:before, .topic_unread_locked:before, .topic_unread_locked_mine:before, .pm_unread:before {
    background-color: #d26187;
}

.pagination li.active span {
    background-color: #d26187;
}

.pagination li a {
    background-color: rgba(255, 255, 255, 0.39);
    color: #3c3c3c;
}

.pagination li a:hover, .pagination .dropdown-visible a.dropdown-trigger, .nojs .pagination .dropdown-container:hover a.dropdown-trigger {
    background-color: rgba(255, 109, 159, 0.6);
}

.panel.bg2 {
    background-color: #d7e0d6;
}

div#postingbox {
    background-color: #d7e0d6;
}

.panel .inputbox {
    background-color: white;
}

select.av-no-app {
    background-color: white;
}

.topicreview#topicreview {
    box-shadow: inset 2px 0px 3px 0px rgba(0,0,0,0.3);
    padding: 0px 14px;
    height: 500px;
}

.topicreview:before {
    content: "";
    position: sticky;
    display: block;
    margin-left: -14px;
    margin-right: 0px;
    top: 0px;
    height: 10px;
    background: none;
    box-shadow: inset 0px 2px 3px 0px rgba(0,0,0,0.2);
    z-index: 10;
}

.topicreview:after {
    content: "";
    position: sticky;
    display: block;
    margin-left: -14px;
    margin-right: 0px;
    bottom: 0px;
    height: 10px;
    background: none;
    box-shadow: inset 0px -2px 3px 0px rgba(0,0,0,0.2);
    z-index: 10;
}

hr {
    border-top-color: rgba(0, 0, 0, 0.12);
}

.panel h2, .panel h3, .panel h4, #page-footer .phpbb_alert h2, #page-footer .phpbb_alert h3 {
    border-bottom: 1px solid rgba(0, 0, 0, 0.12);
}

.postbody .content {
    border-top: 1px solid rgba(0, 0, 0, 0.12);
}

@media (max-width: 1200px) {
.postprofile {
    border-bottom: 1px solid rgba(0, 0, 0, 0.12);
}
}

@media (min-width: 1201px) {
.post.has-profile:before {
    border-left: 1px solid rgba(0, 0, 0, 0.12);
}
}

div#page-footer {
    background-color: #6fa92e;
}

#page-footer .copyright {
    color: #e4e4e4;
}

#page-footer .copyright a {
    color: #ffffff;
}

.submit-buttons input[type="submit"]:not(.default-submit-action) {
    background-color: #5c8a56;    
}


}
