@-moz-document domain("youtube.com")
    {
    /**
    ==UserStyle==
    @name         DARK FADE 2: Dark Theme for YouTube (2022)
    @namespace    org.sidneys.userstyles
    @version      3.0.1
    @description  Modern YouTube version of the classic 'Dark Fade' theme. Uses UserCSS & Polymer Web Components.
    @author       sidneys
    @homepageURL  https://userstyles.org/users/301302
    @supportURL   https://userstyles.org/users/301302
    @license      MIT
    @preprocessor default
    ==/UserStyle==
    */
}

@-moz-document domain("youtube.com")
    {
    /**
     ==========================================================
     VARIABLES
     User
     ==========================================================
     */

    :root
    {
        --width-app-drawer: 240px;
        --width-scrollbar: 1.5rem;
        --border-radius-1: 2px;
        --box-shadow-1: inset 0 4px 5px 0 rgba(0, 0, 0, 0.14), inset 0 1px 10px 0 rgba(0, 0, 0, 0.12), inset 0 2px 4px -1px rgba(0, 0, 0, 0.3);
