        --gradient-3: linear-gradient(to right, var(--color-accent-9) 0%, var(--color-accent-8) 18%, var(--color-accent-7) 35%, var(--color-accent-6) 52%, var(--color-accent-5) 70%, var(--color-accent-4) 85%, var(--color-accent-3) 100%);
