        --gradient-6: linear-gradient(135deg, var(--color-accent-1) 0%, var(--color-accent-3) 2%, var(--color-accent-4) 20%, var(--color-accent-5) 30%, var(--color-accent-6) 52%, var(--color-accent-7) 60%, var(--color-accent-8) 75%, var(--color-accent-9) 80%);
        --filter-none: none;
        --filter-blur-4: blur(4px);
        --filter-blur-8: blur(8px);
        --filter-blur-8-opacity-100: blur(8px) opacity(1.0);
        --filter-opacity-0: opacity(0);
        --filter-opacity-50: opacity(0.5);
        --filter-opacity-75: opacity(0.75);
