style.css:
@-moz-document domain("zybuluo.com") {
.modal-header {
    border-width: 0;
}
.modal-footer {
	 border-width: 0;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    border-radius: 0;
    text-align: left;
}
.btn {
    background: #505871;
    border: none;
    box-shadow: 0 2px 5px #333;
    color: #E0E0E0;
    text-shadow: none;
    box-sizing: border-box;
    border-radius: 3px;
    transition: all .15s;
}
.btn.btn-primary{
    color: #E0E0E0;
    background-color: #2D9CDB;
}
.btn.btn-primary:hover{
    background-color: #2D9CDB;
}
.btn:hover{
    box-shadow: 0 4px 5px #333;
    transform: translate(0,-2px);
}
.btn:hover, .btn:active, .btn.active, .btn.disabled, .btn[disabled] {
    background-color: #505871;
    color: #E0E0E0;
}
.modal{
    border-radius: 3px;
    background-color: #464C62;
}
.modal>div{
    background-color: transparent;
}
.modal-header .close{
    margin: 0;
    padding: 0;
    background-color: #464C62;
    text-shadow: none;
}
.modal-header .close:hover{
    transform: scale(1.2,1.2);
    color: #E0E0E0;
}
}

@-moz-document domain("zybuluo.com") {
.theme-black{
    background-color: #393E50;
}
#wmd-button-bar, #preview-button-bar{
    background-color: #464C62;
}
.wmd-preview{
    border: 1px dashed #464C62;
}
hr{
    border: 1px dashed #464C62;
}
.preview-link button{
    transition: all .15s;
    background-color: transparent;
    text-align: center;
}
.preview-link button:hover{
    font-size: 1.2rem;
    box-shadow: none !important;
}
}
