@-moz-document url-prefix("https://duelingnexus.com/login"), url-prefix("https://duelingnexus.com/register"), url-prefix("https://duelingnexus.com/recover"), url-prefix("https://duelingnexus.com/home"), url-prefix("https://duelingnexus.com/duel"), url-prefix("https://duelingnexus.com/gamelist"), url-prefix("https://duelingnexus.com/hostgame"), url-prefix("https://duelingnexus.com/decks"), url-prefix("https://duelingnexus.com/profile"), url-prefix("https://duelingnexus.com/donate") {
/*SECTION 01 NAVGLOBAL*/
/*This section is global code that applies to all of the navigation pages. For global code for the game and deckbuilding pages, go to GAMEGLOBAL*/
body {
    color: #7D83A3;
    font-family: 'noto sans', sans-serif;
    background-color: rgba(0, 0, 0, 0);
}
html {
    background: url(https://i.ytimg.com/vi/diKLSCstdTw/maxresdefault.jpg) silver 0 0/cover fixed /*Image by fate-fiction*/
}
#home-area h2, #duel-area h2, #gamelist-area h2, #custom-game-area h2, #decks-area h2 {
    position: absolute;
    top: 0;
    left: 2vw;
}
/*Here we have some general clicky thing rules.*/
a {
    color: #7D83A3;
    text-shadow: 0 0 1vw #827C5C, 0 0 2vw #827C5C, 1vw 2vw 3vw black;
}
a:hover, a:focus {
    filter: invert(100%)
}
button, input {
    transition-property: opacity;
    font-family: 'oxygen', sans-serif;
}
form input[type="email"], form input[type="password"], form input[type="text"], input[type="text"], #login-submit, #register-area button, #recover-submit {
    color: #7D83A3;
    background-color: #F0F0F0;
    border-color: #7D83A3;
    box-shadow: -.15vw -.15vw .3vw #7D83A3, -.15vw .15vw .3vw #7D83A3, .15vw -.15vw .3vw #7D83A3, .15vw .15vw .3vw #7D83A3;
    opacity: 0.7;
}
p button:hover, form input[type="email"]:focus, form input[type="password"]:focus, form input[type="text"]:focus, input[type="text"]:focus, #login-submit:focus, #register-area button:focus, #recover-submit:focus {
    filter: invert(100%)
}
form input[type="email"]:hover, form input[type="password"]:hover, form input[type="text"]:hover, input[type="text"]:hover, #recover-email:hover, #login-submit:hover, #register-area button:hover, #recover-submit:hover {
    opacity: 1
}
label {
    font-family: 'noto sans', sans-serif
}
/*This is the code for the navbar, which can be seen from any tab.*/
@keyframes appear {
    0% {opacity: 0}
    50% {opacity: 0}
    100% {opacity: 1}
}
#navbar {
    color: #0F0F0F;
    background: url(https://i.ytimg.com/vi/diKLSCstdTw/maxresdefault.jpg) silver 0 0/cover fixed /*Image by fate-fiction*/;
    filter: invert(100%);
}
#navbar-left button, #navbar-middle button, #navbar-right button {
    animation: appear 3s;
    padding: 0;
}
#navbar-left button, #navbar-middle button, #navbar-right button, #decks-buttons button, .fake-button[data-v-6f705b54], .fake-button[data-v-0262afa4], #my-profile button {
    transition: 2s all;
    color: #827C5C;
    font-weight: bold;
    background-color: rgba(64, 64, 64, .5);
}
#navbar-middle button, #navbar-right button {
    padding: 0 .5vw
}
#navbar-left button:focus, #navbar-middle button:focus, #navbar-right button:focus, #decks-buttons button:focus, .fake-button:focus, #my-profile button:focus {
    font-style: italic;
    border: .15vw dotted;
}
#navbar-left .button:hover, #navbar-middle button:hover, #navbar-right button:hover, #decks-buttons button:hover, .fake-button:hover, #my-profile button:hover {
    transition: 2s all;
    background-color: rgba(191, 191, 191, .5);
    filter: invert(100%);
}
#navbar-middle button:first-of-type {
    display: none
}
/*This removes a black background from elements, and resizes the box to 100% of your screen's width.*/
#home-area, #duel-area, #gamelist-container, #gamelist-area, #custom-game-area[data-v-2bb19dd4], #decks-container, #decks-area {
    background-color: rgba(0, 0, 0, 0);
    border: 0;
    width: 100vw;
    max-width: 100vw;
    margin: 0;
    padding: 5vh 1vw;
    position: absolute;
    top: 8vh;
    left: 0;
}
/*This is table code that sets up for some other sections.*/
table {
    text-align: justify;
    background:  black;
    opacity: 1;
    width: 75vw;
    margin: 45vh 0.1vw 10vh -0.1vw;
    float: right;
    z-index: 2;
    position: relative;
    right: 12.5%;
}
table::before {
    color: antiquewhite;
    font-family: arial, san-serif;
    text-align: center;
    background-size: 100%;
    opacity: .5;
    padding: 0.5vw;
    z-index: 1;
    position: absolute;
    top: -2vw;
    left: 0;
    bottom: 0;
    right: 0; 
}
td {
    z-index: 3;
    position: relative;
}
}

