body {
    font: 2vh/1 'noto sans', arial, sans-serif;
}
html {
    background-image: repeating-linear-gradient(-45deg, transparent, transparent 10px, rgba(75, 75, 75, 0.9) 10px, rgba(0, 0, 0, 0.9) 20px), linear-gradient(45deg, rgba(50, 50, 50, 0.9), rgba(100, 100, 100, 0.9));
    background: url(https://cdn.discordapp.com/attachments/448664507540045827/452513041830117387/11111111.PNG) 0 3vh/100vw 98vh;
 /*Wallpaper by Ayasai.*/;
}
.editor-search-result {
    color: #F0F0F0;
    cursor: pointer;
    background: radial-gradient(rgba(150, 150, 150, 0.5), rgba(50, 50, 50, 0.5));
}
.engine-button {
    -moz-transition-duration: 2s;
    -o-transition-duration: 2s;
    transition-duration: 2s;
    font-family: 'oxygen', arial, sans-serif;
    font-weight: bold;
    color: #F0F0F0;
    background-color: rgba(100, 100, 100, 0.4);
    border: 0;
}
.engine-button:hover, .engine-button:focus {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    background-color: rgba(140, 140, 140, 0.8);
    filter: invert(100%);
}
.engine-button:active {
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    font-style: italic;
    background-color: rgba(145, 145, 145, 0.9);
}
.engine-button-primary {
    background-color: rgba(193, 158, 77, 0.5)
}
.engine-button-primary:hover, .engine-button-primary:focus {
    background-color: rgba(193, 158, 77, 0.7)
}
.engine-button-primary:active {
    background-color: rgba(193, 158, 77, 0.9)
}
.engine-button-danger {
    background-color: rgba(48, 167, 145, 0.5)
}
.engine-button-danger:hover, .engine-button-danger:focus {
    background-color: rgba(48, 167, 145, 0.7)
}
.engine-button-danger:active {
    background-color: rgba(48, 167, 145, 0.9)
}
#editor-menu-container {
    background: repeating-linear-gradient(-45deg, transparent, transparent 10px, rgba(75, 75, 75, 0.9) 10px, rgba(0, 0, 0, 0.9) 20px), linear-gradient(45deg, rgba(50, 50, 50, 0.9), rgba(100, 100, 100, 0.9));
    border: 0;
}
#editor-menu-title {
    background: linear-gradient(#F0F0F0 45%, grey, darkgrey);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color: #F0F0F0;
    font-weight: bold;
    -moz-user-select: none;
    -o-user-select: none;
    user-select: none;
}
#editor-menu-container:after {
    color: #9BAF87;
    content: "Dueling Nexus";
    font: 2vw/6.25 'berkshire swash', papyrus, 'comic sans ms', cursive, sans-serif;
    text-align: center;
    text-shadow: 0 0 0.5em #F0F0F0;
    position: absolute;
    right: 5vw;
    bottom: 0;
    z-index: 2;
}
}
